import React, { useState } from 'react';
import { motion } from 'framer-motion';
import TrackUploader from './TrackUploader';

interface AdminPanelProps {
  onClose: () => void;
}

const AdminPanel: React.FC<AdminPanelProps> = ({ onClose }) => {
  const [activeTab, setActiveTab] = useState<'upload' | 'manage' | 'settings'>('upload');
  const [showUploader, setShowUploader] = useState(false);

  const handleTrackUpload = async (trackData: any, file: File) => {
    // TODO: Implement actual file upload logic
    console.log('Uploading track:', trackData, file);

    // Simulate upload delay
    await new Promise(resolve => setTimeout(resolve, 2000));

    // Here you would:
    // 1. Upload file to /public/tracks/
    // 2. Save metadata to tracks.json
    // 3. Update the app state
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: 20 }}
      className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50"
      onClick={onClose}
    >
      <motion.div
        className="bg-gray-900 rounded-2xl p-6 w-full max-w-6xl max-h-[90vh] overflow-y-auto"
        onClick={(e) => e.stopPropagation()}
      >
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-2xl font-bold text-white">Admin Panel</h2>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-white text-2xl"
          >
            ×
          </button>
        </div>

        {/* Tab Navigation */}
        <div className="flex space-x-1 mb-6 bg-gray-800 p-1 rounded-lg">
          {[
            { id: 'upload', label: 'Upload Tracks', icon: '📤' },
            { id: 'manage', label: 'Manage Tracks', icon: '🎵' },
            { id: 'settings', label: 'Settings', icon: '⚙️' }
          ].map(tab => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id as any)}
              className={`flex-1 px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                activeTab === tab.id
                  ? 'bg-blue-600 text-white'
                  : 'text-gray-400 hover:text-white hover:bg-gray-700'
              }`}
            >
              <span className="mr-2">{tab.icon}</span>
              {tab.label}
            </button>
          ))}
        </div>

        {/* Tab Content */}
        {activeTab === 'upload' && (
          <div className="space-y-6">
            <div className="text-center py-12">
              <div className="text-6xl mb-4">🎵</div>
              <h3 className="text-xl font-semibold text-white mb-2">
                Track Management
              </h3>
              <p className="text-gray-400 mb-6">
                Upload and manage your streaming music library
              </p>
              <button
                onClick={() => setShowUploader(true)}
                className="px-6 py-3 bg-blue-600 hover:bg-blue-700 text-white rounded-lg transition-colors"
              >
                Upload New Track
              </button>
            </div>

            {/* Quick Stats */}
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              <div className="bg-gray-800 p-4 rounded-lg text-center">
                <div className="text-2xl font-bold text-blue-400">0</div>
                <div className="text-gray-400 text-sm">Total Tracks</div>
              </div>
              <div className="bg-gray-800 p-4 rounded-lg text-center">
                <div className="text-2xl font-bold text-green-400">0</div>
                <div className="text-gray-400 text-sm">Approved</div>
              </div>
              <div className="bg-gray-800 p-4 rounded-lg text-center">
                <div className="text-2xl font-bold text-yellow-400">0</div>
                <div className="text-gray-400 text-sm">Pending</div>
              </div>
              <div className="bg-gray-800 p-4 rounded-lg text-center">
                <div className="text-2xl font-bold text-purple-400">0</div>
                <div className="text-gray-400 text-sm">Categories</div>
              </div>
            </div>
          </div>
        )}

        {activeTab === 'manage' && (
          <div className="text-center py-12">
            <div className="text-6xl mb-4">🎵</div>
            <h3 className="text-xl font-semibold text-white mb-2">
              Track Management
            </h3>
            <p className="text-gray-400">
              Coming soon: Edit, delete, and organize your tracks
            </p>
          </div>
        )}

        {activeTab === 'settings' && (
          <div className="text-center py-12">
            <div className="text-6xl mb-4">⚙️</div>
            <h3 className="text-xl font-semibold text-white mb-2">
              Admin Settings
            </h3>
            <p className="text-gray-400">
              Coming soon: Configure admin preferences and permissions
            </p>
          </div>
        )}

        {/* Track Uploader Modal */}
        {showUploader && (
          <TrackUploader
            onTrackUpload={handleTrackUpload}
            onClose={() => setShowUploader(false)}
          />
        )}
      </motion.div>
    </motion.div>
  );
};

export default AdminPanel;
