import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { StreamingTrack, getEnergyLevel, getStreamingCategory } from '../types/track';
import { audioController } from '../services/audioController';
// Removed YouTube Audio Library integration — migrated to Suno API
import DMCAComplianceIndicator from './DMCAComplianceIndicator';

interface EnhancedTrackCardProps {
  track: StreamingTrack;
  onPlay?: () => void;
  onPause?: () => void;
}

const EnhancedTrackCard: React.FC<EnhancedTrackCardProps> = ({ track, onPlay, onPause }) => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(0.8);
  const [showAdvancedControls, setShowAdvancedControls] = useState(false);

  const handlePlayPause = async () => {
    if (isPlaying) {
      audioController.pause();
      setIsPlaying(false);
      onPause?.();
    } else {
      await audioController.playTrack(track, false); // No crossfade for single track
      setIsPlaying(true);
      onPlay?.();
    }
  };

  const handleCrossfade = async () => {
    await audioController.playTrack(track, true); // Enable crossfade
    setIsPlaying(true);
    onPlay?.();
  };

  const handleFadeIn = () => {
    audioController.fadeIn(2); // 2 second fade in
  };

  const handleFadeOut = () => {
    audioController.fadeOut(2); // 2 second fade out
  };

  const handleVolumeChange = (newVolume: number) => {
    setVolume(newVolume);
    audioController.setVolume(newVolume);
  };

  const getEnergyLevelColor = (level: string) => {
    switch (level) {
      case 'low': return 'bg-green-600/20 text-green-400';
      case 'medium': return 'bg-yellow-600/20 text-yellow-400';
      case 'high': return 'bg-red-600/20 text-red-400';
      default: return 'bg-gray-600/20 text-gray-400';
    }
  };

  const getStreamingCategoryColor = (category: string) => {
    switch (category) {
      case 'intro': return 'bg-blue-600/20 text-blue-400';
      case 'background_chat': return 'bg-green-600/20 text-green-400';
      case 'gaming_action': return 'bg-purple-600/20 text-purple-400';
      case 'boss_battle': return 'bg-red-600/20 text-red-400';
      case 'intermission': return 'bg-yellow-600/20 text-yellow-400';
      case 'outro': return 'bg-indigo-600/20 text-indigo-400';
      default: return 'bg-gray-600/20 text-gray-400';
    }
  };

  const formatStreamingCategory = (category: string) => {
    return category.split('_').map(word => 
      word.charAt(0).toUpperCase() + word.slice(1)
    ).join(' ');
  };

  return (
    <motion.div
      className="bg-gray-800 rounded-2xl p-6 border border-gray-700 hover:border-gray-600 transition-all duration-300"
      whileHover={{ scale: 1.02, y: -2 }}
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
    >
      {/* Track Header */}
      <div className="flex items-start justify-between mb-4">
        <div className="flex-1">
          <h3 className="text-xl font-bold text-white mb-2">{track.title}</h3>
          <p className="text-gray-400 text-sm mb-3">Created by Vivid Soundscapes</p>
        </div>

        {/* Advanced Controls Toggle */}
        <button
          onClick={() => setShowAdvancedControls(!showAdvancedControls)}
          className="px-3 py-1 bg-gray-700 hover:bg-gray-600 text-gray-300 rounded-lg text-sm transition-colors"
        >
          {showAdvancedControls ? 'Hide' : 'Advanced'}
        </button>
      </div>

      {/* Track Metadata */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-4">
        <div>
          <p className="text-gray-400 text-sm">Duration</p>
          <p className="text-white font-medium">{track.duration}</p>
        </div>
        <div>
          <p className="text-gray-400 text-sm">Energy</p>
          <span className={`px-2 py-1 rounded-full text-xs font-medium ${getEnergyLevelColor(getEnergyLevel(track))}`}>
            {getEnergyLevel(track)}
          </span>
        </div>
        <div>
          <p className="text-gray-400 text-sm">Category</p>
          <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStreamingCategoryColor(getStreamingCategory(track))}`}>
            {formatStreamingCategory(getStreamingCategory(track))}
          </span>
        </div>
        <div>
          <p className="text-gray-400 text-sm">Mood</p>
          <p className="text-white capitalize">{track.mood}</p>
        </div>
      </div>

      {/* Genre and Description */}
      <div className="mb-4">
        <p className="text-gray-400 text-sm mb-1">Genre</p>
        <p className="text-white capitalize">{track.genre}</p>
        {track.artist && (
          <>
            <p className="text-gray-400 text-sm mb-1 mt-2">Artist</p>
            <p className="text-gray-300 text-sm">{track.artist}</p>
          </>
        )}
      </div>

      {/* Tags */}
      <div className="mb-4">
        <p className="text-gray-400 text-sm mb-2">Tags</p>
        <div className="flex flex-wrap gap-2">
          {track.tags.slice(0, 3).map((tag, index) => (
            <span key={index} className="px-2 py-1 bg-gray-700 text-gray-300 text-xs rounded-full">
              {tag}
            </span>
          ))}
          {track.tags.length > 3 && (
            <span className="px-2 py-1 bg-gray-700 text-gray-300 text-xs rounded-full">
              +{track.tags.length - 3} more
            </span>
          )}
        </div>
      </div>

      {/* Professional Audio Controls */}
      <div className="space-y-4 mb-4">
        {/* Playback Control */}
        <div className="flex items-center justify-between">
          <span className="text-sm text-gray-400 font-medium">Playback Control</span>
          <div className="flex items-center space-x-2">
            <button
              onClick={handlePlayPause}
              className={`px-4 py-2 rounded-xl font-medium transition-all duration-200 ${
                isPlaying
                  ? 'bg-red-600 hover:bg-red-700 text-white'
                  : 'bg-green-600 hover:bg-green-700 text-white'
              }`}
            >
              {isPlaying ? 'Pause' : 'Play'}
            </button>
            <span className="text-xs text-gray-500">
              {isPlaying ? 'Playing' : 'Ready'}
            </span>
          </div>
        </div>

        {/* Professional Audio Features */}
        <div className="grid grid-cols-2 gap-3">
          <button
            onClick={handleCrossfade}
            className="px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg font-medium transition-colors text-sm"
          >
            Crossfade
          </button>

          <button
            onClick={() => {
              // For professional streaming, we keep users within the app
              console.log(`Track ready for streaming: ${track.title}`);
            }}
            className="px-4 py-2 bg-green-600 hover:bg-green-700 text-white rounded-lg font-medium transition-colors text-sm"
          >
            Stream Ready
          </button>
        </div>

        {/* Volume Control */}
        <div className="space-y-2">
          <div className="flex items-center justify-between">
            <label className="text-sm text-gray-400 font-medium">Volume</label>
            <span className="text-xs text-gray-500">{Math.round(volume * 100)}%</span>
          </div>
          <input
            type="range"
            min="0"
            max="1"
            step="0.01"
            value={volume}
            onChange={(e) => handleVolumeChange(Number(e.target.value))}
            className="w-full h-2 bg-gray-700 rounded-lg appearance-none cursor-pointer slider"
          />
        </div>
      </div>

      {/* DMCA Compliance Indicator */}
      <div className="border-t border-gray-700 pt-4">
        <DMCAComplianceIndicator 
          track={track} 
          showDetails={true}
          onTrackUsage={(trackId) => {
            console.log(`Track usage tracked: ${trackId}`);
            // In production, this would send data to your analytics system
          }}
        />
      </div>

      {/* Advanced Controls */}
      {showAdvancedControls && (
        <motion.div
          initial={{ opacity: 0, height: 0 }}
          animate={{ opacity: 1, height: 'auto' }}
          exit={{ opacity: 0, height: 0 }}
          className="border-t border-gray-700 pt-4 space-y-3"
        >
          <h4 className="text-lg font-medium text-white mb-3">Advanced Audio Controls</h4>

          <div className="grid grid-cols-2 gap-3">
            <button
              onClick={handleFadeIn}
              className="px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg transition-colors"
            >
              Fade In (2s)
            </button>
            <button
              onClick={handleFadeOut}
              className="px-4 py-2 bg-red-600 hover:bg-red-700 text-white rounded-lg transition-colors"
            >
              Fade Out (2s)
            </button>
          </div>

          {/* Loop Controls */}
          <div className="flex items-center space-x-3">
            <input
              type="checkbox"
              id={`loop-${track.id}`}
              onChange={(e) => audioController.setLooping(e.target.checked, 1)}
              className="rounded border-gray-600 text-blue-600 focus:ring-blue-500"
            />
            <label htmlFor={`loop-${track.id}`} className="text-sm font-medium text-gray-300">
              Enable Looping
            </label>
          </div>

          {/* Audio Ducking Status */}
          <div className="flex items-center space-x-3">
            <div className={`w-3 h-3 rounded-full ${
              audioController.getSettings().duckingEnabled ? 'bg-green-500' : 'bg-gray-500'
            }`} />
            <span className="text-sm text-gray-300">
              Audio Ducking: {audioController.getSettings().duckingEnabled ? 'Enabled' : 'Disabled'}
            </span>
          </div>

          {/* Normalization Status */}
          <div className="flex items-center space-x-3">
            <div className={`w-3 h-3 rounded-full ${
              audioController.getSettings().normalizationEnabled ? 'bg-green-500' : 'bg-gray-500'
            }`} />
            <span className="text-sm text-gray-300">
              Audio Normalization: {audioController.getSettings().normalizationEnabled ? 'Enabled' : 'Disabled'}
            </span>
          </div>
        </motion.div>
      )}

      {/* Removed YouTube Audio Library integration — migrated to Suno API */}
      {/* Audio player will be implemented for Suno API tracks */}
    </motion.div>
  );
};

export default EnhancedTrackCard;
