import { StreamingTrack } from '../types/track';

// Suno.ai API Configuration
const SUNO_API_KEY = (import.meta as any).env?.VITE_SUNO_API_KEY || process.env.REACT_APP_SUNO_API_KEY;
const SUNO_API_BASE = 'https://api.sunoapi.org/api/v1';

// Rich, styled prompt templates for different streaming categories
const CATEGORY_PROMPTS: CategoryPrompts = {
  stream_starting_soon: [
    "Upbeat electronic with driving synths, fast tempo (140-160 BPM), exciting energy, perfect for stream countdowns. Include build-ups and positive vibes for audience anticipation.",
    "Energetic gaming intro music with modern electronic beats, festival-style drops, and euphoric melodies. High-energy track designed to get viewers hyped for the stream.",
    "Epic orchestral intro music with cinematic strings, powerful brass, and dramatic percussion. Builds excitement and sets the stage for an amazing stream experience."
  ],
  chill_gaming: [
    "Lo-fi hip hop with mellow beats, warm piano, smooth bass, and vinyl crackle. Relaxed mood (70-85 BPM) with jazzy samples, perfect for casual gaming sessions.",
    "Ambient electronic music with soft pads, gentle arpeggios, and atmospheric textures. Peaceful and non-distracting, creates a calm gaming environment.",
    "Calm acoustic guitar with soft electronic elements, fingerpicking patterns, and subtle percussion. Organic feel with modern touches for relaxed gaming."
  ],
  gaming_action: [
    "Intense electronic music with heavy bass, aggressive synths, and driving percussion. High energy (150-170 BPM) with drops and build-ups for action gaming scenes.",
    "Epic orchestral battle music with dramatic percussion, choirs, and intense cinematic build-ups. Powerful brass and strings for competitive gaming moments.",
    "Fast-paced electronic beats with aggressive drums, distorted bass, and energetic melodies. Designed for high-intensity gaming and competitive play."
  ],
  hype_raid: [
    "Energetic EDM with heavy bass drops, festival-style synths, and euphoric melodies. High-energy vibe (140-160 BPM) perfect for raid celebrations and hype moments.",
    "Epic orchestral music with dramatic percussion, choirs, and cinematic build-ups. Designed to create excitement and celebration during raid events.",
    "High-energy electronic beats with powerful drops, uplifting melodies, and driving rhythms. Perfect for creating hype and energy during raid moments."
  ],
  break_brb: [
    "Calm ambient with soft pads, light piano, and gentle textures. Very low tempo (60-75 BPM), peaceful intermission atmosphere with subtle movement.",
    "Soft instrumental music with gentle strings, light percussion, and warm tones. Soothing and non-intrusive, perfect for break screens and BRB moments.",
    "Chill electronic music with ambient pads, soft arpeggios, and minimal percussion. Quiet and non-intrusive, creates a peaceful break atmosphere."
  ],
  talk_show: [
    "Light jazz with upright bass, soft drums, and piano. Conversational and background-friendly (80-100 BPM), sophisticated but not distracting.",
    "Soft instrumental music with gentle guitar, light percussion, and warm tones. Professional and unobtrusive, perfect for discussions and interviews.",
    "Ambient electronic music with subtle textures, soft pads, and minimal percussion. Background-friendly with sophisticated atmosphere for talk shows."
  ],
  intro_outro: [
    "Epic synthwave with powerful intro build, strong melodies, and dramatic outro fade. Stream branding style with memorable hooks and professional production.",
    "Modern electronic intro/outro music with energetic beats, powerful bass, and catchy melodies. Professional and engaging for stream branding.",
    "Classic rock-inspired intro/outro music with powerful guitars, driving drums, and strong vocals. Engaging and memorable for stream identity."
  ],
  boss_battle: [
    "Intense orchestral boss battle music with dramatic percussion, choirs, and cinematic build-ups. Dark and powerful atmosphere for epic boss fights.",
    "Epic electronic boss battle music with heavy bass, aggressive synths, and dramatic drops. High energy and intense for challenging boss encounters.",
    "Dark orchestral music with ominous strings, powerful percussion, and dramatic brass. Creates tension and excitement for boss battle moments."
  ],
  intermission: [
    "Calm instrumental intermission music with soft strings, gentle piano, and light percussion. Peaceful and relaxing for smooth transitions.",
    "Soft electronic intermission music with ambient pads, gentle arpeggios, and minimal beats. Atmospheric and unobtrusive for intermission periods.",
    "Chill acoustic intermission music with fingerpicked guitar, soft percussion, and warm tones. Organic and peaceful for transition moments."
  ],
  background_chat: [
    "Very quiet ambient soundscape with subtle textures, soft pads, and minimal movement. Extremely low volume background music that doesn't interfere with chat.",
    "Soft instrumental background music with gentle piano, light strings, and minimal percussion. Barely audible but adds atmosphere to chat sessions.",
    "Gentle electronic background music with ambient textures, soft arpeggios, and minimal beats. Non-distracting background music for chat-focused content."
  ]
};

// Fallback prompt for unmapped categories
const GENERIC_SAFE_PROMPT = "DMCA-safe background music suitable for streaming. Gentle instrumental with soft melodies, non-distracting and platform-compliant.";

// Legacy prompts for backward compatibility (deprecated)
const GENERATION_PROMPTS = CATEGORY_PROMPTS;

// Category prompts interface for type safety
interface CategoryPrompts {
  [category: string]: string[];
}

// Suno.ai API response interface
interface SunoGenerationResponse {
  id: string;
  status: 'pending' | 'processing' | 'completed' | 'failed';
  audio_url?: string;
  metadata?: {
    prompt: string;
    duration: number;
    tags: string[];
  };
  error?: string;
}

// Generation request interface
interface GenerationRequest {
  prompt: string;
  duration?: number; // in seconds
  tags?: string[];
  category: string;
  mood: string;
  genre: string;
}

class SunoService {
  private apiKey: string;
  private isInitialized: boolean = false;

  constructor() {
    this.apiKey = SUNO_API_KEY || '';
    this.isInitialized = !!this.apiKey;

    if (!this.isInitialized) {
      console.log('‚ÑπÔ∏è Suno.ai API key not found - using demo mode with sample tracks');
      console.log('üí° To enable AI music generation, set REACT_APP_SUNO_API_KEY environment variable');
      console.log('üîó Get your API key at: https://suno.ai/');
    }
  }

  // Initialize the service
  async initialize(): Promise<boolean> {
    if (!this.apiKey) {
      console.log('‚ÑπÔ∏è Suno.ai API key not available - running in demo mode');
      console.log('üéµ You can still use sample tracks and test the interface');
      return false;
    }

    try {
      // Test API connection with proper endpoint
      const response = await fetch(`${SUNO_API_BASE}/generate`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${this.apiKey}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          prompt: "Test connection - simple instrumental music",
          customMode: false,
          instrumental: true,
          model: "V4"
        })
      });

      if (response.status === 401) {
        console.error('‚ùå Suno API authentication failed ‚Äî check your API key and Bearer header');
        return false;
      }

      if (response.ok) {
        console.log('‚úÖ Suno.ai API connection successful');
        this.isInitialized = true;
        return true;
      } else {
        console.error(`‚ùå Suno.ai API connection failed: ${response.status} ${response.statusText}`);
        return false;
      }
    } catch (error) {
      console.error('‚ùå Suno.ai API connection error:', error);
      return false;
    }
  }

  // Generate a new track
  async generateTrack(request: GenerationRequest): Promise<SunoGenerationResponse> {
    if (!this.isInitialized) {
      throw new Error('Suno.ai API key required for track generation. Set REACT_APP_SUNO_API_KEY to enable AI music creation.');
    }

    try {
      console.log('üéµ Generating track with Suno.ai:', request.prompt);

      const response = await fetch(`${SUNO_API_BASE}/generate`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${this.apiKey}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          prompt: request.prompt,
          customMode: false,
          instrumental: true,
          model: "V4"
        })
      });

      if (response.status === 401) {
        console.error('‚ùå Suno API authentication failed ‚Äî check your API key and Bearer header');
        throw new Error('Authentication failed - check API key');
      }

      if (!response.ok) {
        throw new Error(`Suno.ai API error: ${response.status} ${response.statusText}`);
      }

      const result: SunoGenerationResponse = await response.json();
      console.log('üéµ Track generation started:', result.id);

      return result;
    } catch (error) {
      console.error('‚ùå Track generation failed:', error);
      throw error;
    }
  }

  // Check generation status
  async checkGenerationStatus(generationId: string): Promise<SunoGenerationResponse> {
    if (!this.isInitialized) {
      throw new Error('Suno.ai API key required for status checking. Set REACT_APP_SUNO_API_KEY to enable AI music features.');
    }

    try {
      const response = await fetch(`${SUNO_API_BASE}/generations/${generationId}`, {
        headers: {
          'Authorization': `Bearer ${this.apiKey}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error(`Suno.ai API error: ${response.status} ${response.statusText}`);
      }

      const result: SunoGenerationResponse = await response.json();
      return result;
    } catch (error) {
      console.error('‚ùå Status check failed:', error);
      throw error;
    }
  }

  // Wait for generation to complete
  async waitForGeneration(generationId: string, maxWaitTime: number = 300000): Promise<SunoGenerationResponse> {
    const startTime = Date.now();

    while (Date.now() - startTime < maxWaitTime) {
      const status = await this.checkGenerationStatus(generationId);

      if (status.status === 'completed') {
        console.log('‚úÖ Track generation completed:', generationId);
        return status;
      } else if (status.status === 'failed') {
        throw new Error(`Track generation failed: ${status.error}`);
      }

      // Wait 5 seconds before checking again
      await new Promise(resolve => setTimeout(resolve, 5000));
    }

    throw new Error('Track generation timeout');
  }

  // Generate track for a specific category
  async generateTrackForCategory(category: string, mood: string, genre: string): Promise<StreamingTrack> {
    // Get styled prompts for the category, with fallback to generic safe prompt
    const prompts = getPromptsForCategory(category);

    // Select a random styled prompt for variety
    const prompt = prompts[Math.floor(Math.random() * prompts.length)];

    const request: GenerationRequest = {
      prompt,
      category,
      mood,
      genre,
      duration: this.getDurationForCategory(category)
    };

    console.log('üéµ Generating track for category:', category);
    console.log('üéµ Prompt:', prompt);

    const generation = await this.generateTrack(request);
    const result = await this.waitForGeneration(generation.id);

    if (!result.audio_url) {
      throw new Error('No audio URL in generation result');
    }

    // Create a StreamingTrack from the generated audio
    const track: StreamingTrack = {
            id: `suno_${generation.id}`,
      title: this.generateTitle(category, mood, genre),
      artist: `AI-generated track by Suno.ai - ${prompt}`,
      duration: result.metadata?.duration || 180,
      audioUrl: result.audio_url,
      category: 'gaming', // Default category
      subcategory: 'ambient', // Default subcategory
      mood: mood as any,
      energy: this.getEnergyLevelForCategory(category) === 'high' ? 4 : 
              this.getEnergyLevelForCategory(category) === 'medium' ? 3 : 2,
      tags: result.metadata?.tags || [category, mood, genre],
      streamSafe: true,
      loopFriendly: true,
      hasIntro: false,
      hasOutro: false,
      uploadDate: new Date().toISOString(),
      uploadedBy: 'system',
      approved: true,
      // Legacy fields for backward compatibility
      sunoId: generation.id, // Suno API generation ID
      streamingCategory: category as any,
      energyLevel: this.getEnergyLevelForCategory(category),
      genre: genre as any,
      dmcaSafe: true,
      vodSafe: true,
      license: 'royalty_free',
      licenseDetails: 'AI-generated music by Suno.ai - Royalty-free for commercial use',
      platformCompliance: {
        twitch: 'safe',
        youtube: 'safe',
        facebook: 'safe',
        tiktok: 'safe'
      },
      usageTracking: {
        usageCount: 0
      },
      agencyNotes: 'AI-generated track - DMCA safe for all platforms'
    };

    return track;
  }

  // Generate multiple tracks for a category
  async generateTracksForCategory(category: string, count: number = 5): Promise<StreamingTrack[]> {
    const tracks: StreamingTrack[] = [];
    const moods = ['chill', 'energetic', 'epic', 'peaceful', 'mysterious'];
    const genres = ['electronic', 'orchestral', 'ambient', 'acoustic', 'cinematic'];

    for (let i = 0; i < count; i++) {
      try {
        const mood = moods[i % moods.length];
        const genre = genres[i % genres.length];

        const track = await this.generateTrackForCategory(category, mood, genre);
        tracks.push(track);

        console.log(`‚úÖ Generated track ${i + 1}/${count}: ${track.title}`);

        // Wait between generations to avoid rate limits
        if (i < count - 1) {
          await new Promise(resolve => setTimeout(resolve, 10000)); // 10 second delay
        }
      } catch (error) {
        console.error(`‚ùå Failed to generate track ${i + 1}:`, error);
      }
    }

    return tracks;
  }

  // Generate a complete library for all categories
  async generateCompleteLibrary(tracksPerCategory: number = 3): Promise<StreamingTrack[]> {
    const allTracks: StreamingTrack[] = [];
    const categories = Object.keys(CATEGORY_PROMPTS);

    console.log(`üéµ Generating complete library: ${categories.length} categories √ó ${tracksPerCategory} tracks`);

    for (const category of categories) {
      try {
        console.log(`üéµ Generating tracks for category: ${category}`);
        const categoryTracks = await this.generateTracksForCategory(category, tracksPerCategory);
        allTracks.push(...categoryTracks);

        console.log(`‚úÖ Generated ${categoryTracks.length} tracks for ${category}`);

        // Wait between categories
        await new Promise(resolve => setTimeout(resolve, 30000)); // 30 second delay
      } catch (error) {
        console.error(`‚ùå Failed to generate tracks for category ${category}:`, error);
      }
    }

    console.log(`‚úÖ Complete library generated: ${allTracks.length} tracks`);
    return allTracks;
  }

  // Helper methods
  private getDurationForCategory(category: string): number {
    const durations: { [key: string]: number } = {
      stream_starting_soon: 180, // 3 minutes
      intro_outro: 120, // 2 minutes
      break_brb: 180, // 3 minutes
      intermission: 180, // 3 minutes
      background_chat: 300, // 5 minutes
      chill_gaming: 300, // 5 minutes
      gaming_action: 240, // 4 minutes
      hype_raid: 180, // 3 minutes
      talk_show: 300, // 5 minutes
      boss_battle: 240 // 4 minutes
    };

    return durations[category] || 180;
  }

  private getEnergyLevelForCategory(category: string): 'low' | 'medium' | 'high' {
    const highEnergy = ['gaming_action', 'hype_raid', 'boss_battle', 'intro_outro'];
    const lowEnergy = ['chill_gaming', 'break_brb', 'intermission', 'background_chat', 'talk_show'];

    if (highEnergy.includes(category)) return 'high';
    if (lowEnergy.includes(category)) return 'low';
    return 'medium';
  }

  private formatDuration(seconds: number): string {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  }

  private generateTitle(category: string, mood: string, genre: string): string {
    const categoryNames: { [key: string]: string } = {
      stream_starting_soon: 'Stream Starting Soon',
      chill_gaming: 'Chill Gaming',
      gaming_action: 'Gaming Action',
      hype_raid: 'Hype Raid',
      break_brb: 'Break/BRB',
      talk_show: 'Talk Show',
      intro_outro: 'Intro/Outro',
      boss_battle: 'Boss Battle',
      intermission: 'Intermission',
      background_chat: 'Background Chat'
    };

    const categoryName = categoryNames[category] || category;
    return `${categoryName} - ${mood.charAt(0).toUpperCase() + mood.slice(1)} ${genre.charAt(0).toUpperCase() + genre.slice(1)}`;
  }

  // Get service status
  getStatus(): { initialized: boolean; hasApiKey: boolean } {
    return {
      initialized: this.isInitialized,
      hasApiKey: !!this.apiKey
    };
  }

  public isDemoMode(): boolean {
    return !this.isInitialized;
  }

  public getDemoMessage(): string {
    if (this.isDemoMode()) {
      return 'üéµ Demo Mode: Set REACT_APP_SUNO_API_KEY to enable AI music generation';
    }
    return '';
  }
}

// Export singleton instance
const sunoService = new SunoService();
export default sunoService;

// Helper function to get prompts with fallback
const getPromptsForCategory = (category: string): string[] => {
  const prompts = CATEGORY_PROMPTS[category];
  if (prompts && prompts.length > 0) {
    return prompts;
  }
  console.warn(`‚ö†Ô∏è Category "${category}" not found in CATEGORY_PROMPTS, using generic safe prompt`);
  return [GENERIC_SAFE_PROMPT];
};

// Streaming categories for compatibility
export const STREAMING_CATEGORIES = {
  STREAM_STARTING_SOON: 'stream_starting_soon',
  CHILL_GAMING: 'chill_gaming',
  GAMING_ACTION: 'gaming_action',
  HYPE_RAID: 'hype_raid',
  BREAK_BRB: 'break_brb',
  TALK_SHOW: 'talk_show',
  INTRO_OUTRO: 'intro_outro',
  BOSS_BATTLE: 'boss_battle',
  INTERMISSION: 'intermission',
  BACKGROUND_CHAT: 'background_chat'
} as const;

// Get all available streaming categories
export const getStreamingCategories = () => {
  return Object.values(STREAMING_CATEGORIES);
};

// Get display name for category
export const getCategoryDisplayName = (category: string): string => {
  const displayNames: { [key: string]: string } = {
    stream_starting_soon: 'Stream Starting Soon',
    chill_gaming: 'Chill Gaming',
    gaming_action: 'Gaming Action',
    hype_raid: 'Hype Raid',
    break_brb: 'Break/BRB',
    talk_show: 'Talk Show',
    intro_outro: 'Intro/Outro',
    boss_battle: 'Boss Battle',
    intermission: 'Intermission',
    background_chat: 'Background Chat'
  };

  return displayNames[category] || category;
};

// Export types and constants
export {
  CATEGORY_PROMPTS,
  GENERIC_SAFE_PROMPT,
  GENERATION_PROMPTS, // Legacy export for backward compatibility
  getPromptsForCategory,
  type SunoGenerationResponse,
  type GenerationRequest,
  type CategoryPrompts
};
