export interface StreamingTrack {
  id: string;
  title: string;
  artist: string;
  duration: number;
  audioUrl: string;
  coverUrl?: string;

  // Streaming-specific metadata
  category: 'gaming' | 'chat' | 'intro-outro' | 'energy' | 'ambient';
  subcategory: string; // 'boss-battle', 'exploration', 'menu', etc.
  mood: string; // 'epic', 'chill', 'intense', 'peaceful'
  energy: 1 | 2 | 3 | 4 | 5; // 1=very chill, 5=high energy
  bpm?: number;
  key?: string;
  tags: string[];
  description?: string;

  // Usage metadata
  streamSafe: boolean;
  loopFriendly: boolean;
  hasIntro: boolean;
  hasOutro: boolean;

  // Admin metadata
  uploadDate: string;
  uploadedBy: string;
  approved: boolean;

  // Legacy fields for backward compatibility
  sunoId?: string;
  streamingCategory?: string;
  genre?: string;
  energyLevel?: string; // For backward compatibility
  dmcaSafe?: boolean;
  vodSafe?: boolean;
  license?: string;
  licenseDetails?: string;
  platformCompliance?: {
    twitch: string;
    youtube: string;
    facebook: string;
    tiktok: string;
  };
  usageTracking?: {
    usageCount: number;
    lastUsed?: Date;
  };
  agencyNotes?: string;
}

export interface Category {
  id: string;
  name: string;
  description: string;
  color: string;
  icon: string;
  subcategories: string[];
}

export interface FilterOptions {
  category?: string;
  subcategory?: string;
  energy?: number[];
  mood?: string[];
  duration?: { min: number; max: number };
  bpm?: { min: number; max: number };
  loopFriendly?: boolean;
  tags?: string[];
}

export interface AgencyTheme {
  id: string;
  name: string;
  primaryColor: string;
  secondaryColor: string;
  logo: string;
  customCSS?: string;
  brandedCategories?: { [key: string]: string };
}

// Legacy interface for backward compatibility
export interface LegacyTrack {
  id: string;
  title: string;
  artist: string;
  duration: number;
  audioUrl: string;
  sunoId?: string;
  streamingCategory: string;
  genre: string;
  mood: string;
  energyLevel: string;
  tags: string[];
  description: string;
  dmcaSafe: boolean;
  vodSafe: boolean;
  license: string;
  licenseDetails: string;
  platformCompliance: {
    twitch: string;
    youtube: string;
    facebook: string;
    tiktok: string;
  };
  usageTracking: {
    usageCount: number;
    lastUsed?: Date;
  };
  agencyNotes: string;
}

// Helper functions for backward compatibility
export const getEnergyLevel = (track: StreamingTrack): string => {
  if (track.energyLevel) return track.energyLevel;
  if (track.energy) {
    if (track.energy <= 2) return 'low';
    if (track.energy <= 3) return 'medium';
    return 'high';
  }
  return 'medium';
};

export const getEnergyLevelColor = (level: string): string => {
  switch (level.toLowerCase()) {
    case 'low': return 'bg-green-600/20 text-green-400 border-green-600/30';
    case 'medium': return 'bg-yellow-600/20 text-yellow-400 border-yellow-600/30';
    case 'high': return 'bg-red-600/20 text-red-400 border-red-600/30';
    default: return 'bg-gray-600/20 text-gray-400 border-gray-600/30';
  }
};

export const getStreamingCategory = (track: StreamingTrack): string => {
  return track.streamingCategory || track.category || 'unknown';
};
