import { useState, useMemo, useEffect, useCallback } from 'react';
import { motion } from 'framer-motion';
import { StreamingTrack } from '../types/track';
// Removed YouTube Audio Library integration ‚Äî migrated to Suno API
import { getStreamingCategories, getCategoryDisplayName } from '../services/sunoService';
import sunoService from '../services/sunoService';
import { runPerformanceDiagnostics } from '../utils/performanceTest';
import analyticsService from '../services/analyticsService';
import webhookService from '../services/webhookService';
import AdminPanel from './admin/AdminPanel';
import { getEnergyLevel } from '../types/track';
import AnalyticsDashboard from './AnalyticsDashboard';
import TrackCard from './TrackCard';
// import LightweightTrackCard from './LightweightTrackCard';
import UltraLightTrackCard from './UltraLightTrackCard';
// Removed YouTube Audio Library integration ‚Äî migrated to Suno API
import SunoGenerator from './SunoGenerator';
import RecentlyGeneratedTracks from './RecentlyGeneratedTracks';

// Performance monitoring utilities
const performanceMonitor = {
  startTime: 0,
  jsonLoadTime: 0,
  trackProcessingTime: 0,
  memoryUsage: 0,

  start() {
    this.startTime = performance.now();
    console.time('Total Load Time');
    console.time('JSON Load Time');
    console.log('Available Memory:', (performance as any).memory?.usedJSHeapSize);
    console.log('JSON File Size Check');
  },

  endJsonLoad() {
    console.timeEnd('JSON Load Time');
    this.jsonLoadTime = performance.now() - this.startTime;
    console.time('Track Processing Time');
  },

  endTrackProcessing() {
    console.timeEnd('Track Processing Time');
    this.trackProcessingTime = performance.now() - this.startTime;
    console.timeEnd('Total Load Time');
    this.memoryUsage = (performance as any).memory?.usedJSHeapSize || 0;
    console.log('Final Memory Usage:', this.memoryUsage);
  }
};

// Performance configuration for older hardware
const PERFORMANCE_CONFIG = {
  INITIAL_BATCH_SIZE: 3, // Very small initial load for 2013 MacBook Pro
  LOAD_MORE_BATCH_SIZE: 5, // Tiny batches
  VIRTUALIZATION_WINDOW_SIZE: 2, // Only 2 visible items
  LAZY_LOAD_DELAY: 500, // Much longer delay between batches
  PERFORMANCE_MODE_THRESHOLD: 1, // Very low threshold
};

// Removed YouTube Audio Library integration ‚Äî migrated to Suno API
// Sample tracks for fallback (Suno API generated)
const SAMPLE_TRACKS: StreamingTrack[] = [
  {
    id: 'sample_1',
    title: 'AI Generated Chill Track',
    artist: 'AI-generated sample track for testing',
    duration: 150,
    audioUrl: 'https://api.sunoapi.org/sample1.mp3',
    category: 'gaming',
    subcategory: 'chill',
    mood: 'chill',
    energy: 2,
    tags: ['ambient', 'chill', 'chill_gaming', 'low'],
    streamSafe: true,
    loopFriendly: true,
    hasIntro: false,
    hasOutro: false,
    uploadDate: new Date().toISOString(),
    uploadedBy: 'system',
    approved: true,
    // Legacy fields for backward compatibility
    sunoId: 'sample1',
    streamingCategory: 'chill_gaming',
    energyLevel: 'low',
    genre: 'ambient',
    dmcaSafe: true,
    vodSafe: true,
    license: 'royalty_free',
    licenseDetails: 'Suno AI generated track',
    platformCompliance: {
      twitch: 'safe',
      youtube: 'safe',
      facebook: 'safe',
      tiktok: 'safe'
    },
    usageTracking: {
      usageCount: 0,
      lastUsed: undefined
    },
    agencyNotes: 'AI-generated sample track'
  },
  {
    id: 'sample_2',
    title: 'AI Generated Energetic Track',
    artist: 'AI-generated energetic track for testing',
    duration: 195,
    audioUrl: 'https://api.sunoapi.org/sample2.mp3',
    category: 'gaming',
    subcategory: 'action',
    mood: 'energetic',
    energy: 4,
    tags: ['electronic', 'energetic', 'gaming_action', 'high'],
    streamSafe: true,
    loopFriendly: true,
    hasIntro: false,
    hasOutro: false,
    uploadDate: new Date().toISOString(),
    uploadedBy: 'system',
    approved: true,
    // Legacy fields for backward compatibility
    sunoId: 'sample2',
    streamingCategory: 'gaming_action',
    energyLevel: 'high',
    genre: 'electronic',
    dmcaSafe: true,
    vodSafe: true,
    license: 'royalty_free',
    licenseDetails: 'Suno AI generated track',
    platformCompliance: {
      twitch: 'safe',
      youtube: 'safe',
      facebook: 'safe',
      tiktok: 'safe'
    },
    usageTracking: {
      usageCount: 0,
      lastUsed: undefined
    },
    agencyNotes: 'Sample track'
  }
];

const StreamSoundboard: React.FC = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedMood, setSelectedMood] = useState<string>('all');
  const [selectedCategory, setSelectedCategory] = useState<string>('all');
  const [selectedEnergy, setSelectedEnergy] = useState<string>('all');
  const [tracks, setTracks] = useState<StreamingTrack[]>([]);
  const [allTracks, setAllTracks] = useState<StreamingTrack[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [loadedCount, setLoadedCount] = useState(0);
  const [totalTracks, setTotalTracks] = useState(0);
  const [showDebugPanel, setShowDebugPanel] = useState(false);

  // Performance mode state
  const [performanceMode, setPerformanceMode] = useState<'auto' | 'low' | 'full'>('auto');
  const [virtualizationEnabled, setVirtualizationEnabled] = useState(false);
  const [visibleTrackRange] = useState({ start: 0, end: 5 });

  // Shared audio state
  const [currentPlayingTrack, setCurrentPlayingTrack] = useState<StreamingTrack | null>(null);
  const [isAudioPlaying, setIsAudioPlaying] = useState(false);
  const [showSunoGenerator, setShowSunoGenerator] = useState(false);
  const [showAdminPanel, setShowAdminPanel] = useState(false);

  // Performance state
  const [performanceMetrics, setPerformanceMetrics] = useState({
    jsonLoadTime: 0,
    trackProcessingTime: 0,
    memoryUsage: 0,
    fileSize: '0 KB'
  });

  // Check API key status safely
  const apiKeyStatus = process.env.REACT_APP_YOUTUBE_API_KEY ? '‚úÖ Configured' : '‚ùå Missing';

  // Detect hardware capabilities and set performance mode
  useEffect(() => {
    const hardwareConcurrency = navigator.hardwareConcurrency || 1;
    const deviceMemory = (navigator as any).deviceMemory || 4;

    // Force low performance mode for 2013 MacBook Pro
    if (performanceMode === 'auto') {
      // 2013 MacBook Pro typically has 2-4 cores, so we'll force low mode
      setPerformanceMode('low');
      setVirtualizationEnabled(true);
      console.log('üîß Forcing low performance mode for 2013 MacBook Pro');
      console.log(`üîß Hardware: ${hardwareConcurrency} cores, ${deviceMemory}GB RAM`);
    }
  }, [performanceMode]);

  // Debounced search function for performance
  const debouncedSearch = useCallback((value: string) => {
    // simple 300ms debounce per call
    window.clearTimeout((debouncedSearch as any)._t);
    (debouncedSearch as any)._t = window.setTimeout(() => {
      setSearchTerm(value);
      if (value.length > 0) {
        analyticsService.trackUserInteraction('search_tracks', {
          searchTerm: value,
          searchLength: value.length,
          tracksLoaded: tracks.length
        });
      }
    }, 300);
  }, [tracks.length]);

  // Memory-safe loading with batching - optimized for older hardware
  const loadTracksInBatches = useCallback(async (allTracksData: StreamingTrack[], batchSize: number = PERFORMANCE_CONFIG.INITIAL_BATCH_SIZE) => {
    const total = allTracksData.length;
    setTotalTracks(total);

    // Load first batch immediately
    const firstBatch = allTracksData.slice(0, batchSize);
    setTracks(firstBatch);
    setLoadedCount(batchSize);

    // Set up remaining batches
    const remainingTracks = allTracksData.slice(batchSize);
    const batches = [];

    for (let i = 0; i < remainingTracks.length; i += PERFORMANCE_CONFIG.LOAD_MORE_BATCH_SIZE) {
      batches.push(remainingTracks.slice(i, i + PERFORMANCE_CONFIG.LOAD_MORE_BATCH_SIZE));
    }

    // Load remaining batches with delay to prevent blocking
    for (let i = 0; i < batches.length; i++) {
      await new Promise(resolve => setTimeout(resolve, PERFORMANCE_CONFIG.LAZY_LOAD_DELAY));
      const newBatch = batches[i];
      setTracks(prev => [...prev, ...newBatch]);
      setLoadedCount(prev => prev + newBatch.length);
    }
  }, []);

  // Load curated tracks from YouTube service with performance monitoring
  useEffect(() => {
    const loadTracks = async () => {
      try {
        performanceMonitor.start();

        // Set timeout for loading
        const timeout = setTimeout(() => {
          setError('Loading is taking longer than expected. This might be due to your device or connection. Try refreshing the page.');

          // Track timeout in analytics
          analyticsService.trackPerformanceIssue({
            type: 'timeout',
            details: 'App loading exceeded 10 second timeout',
            metrics: {
              timeoutDuration: 10000,
              jsonLoadTime: performanceMonitor.jsonLoadTime,
              trackProcessingTime: performanceMonitor.trackProcessingTime,
              totalTime: performance.now() - performanceMonitor.startTime
            }
          });

          setLoading(false);
        }, 10000);


        // Removed YouTube Audio Library integration ‚Äî migrated to Suno API
        console.log('üîç Loading tracks from Suno API...');

        performanceMonitor.endJsonLoad();

        // For now, use sample tracks until Suno API integration is complete
        const tracksData = SAMPLE_TRACKS;

        performanceMonitor.endTrackProcessing();

        // Update performance metrics
        const finalMetrics = {
          jsonLoadTime: performanceMonitor.jsonLoadTime,
          trackProcessingTime: performanceMonitor.trackProcessingTime,
          memoryUsage: performanceMonitor.memoryUsage,
          fileSize: '304 KB' // Known file size
        };

        setPerformanceMetrics(finalMetrics);

        setAllTracks(tracksData);

        // Load tracks in batches for better performance
        await loadTracksInBatches(tracksData);

        // Track successful app load in analytics
        analyticsService.trackAppLoad({
          jsonLoadTime: finalMetrics.jsonLoadTime,
          trackProcessingTime: finalMetrics.trackProcessingTime,
          totalLoadTime: performance.now() - performanceMonitor.startTime,
          tracksLoaded: tracksData.length,
          totalTracks: tracksData.length,
          loadSuccess: true
        });

        console.log(`‚úÖ Loaded ${tracksData.length} tracks`);
        console.log('üîç First few tracks loaded:', tracksData.slice(0, 3));
        console.log('üîç Track sources:', tracksData.map(t => ({ id: t.id, title: t.title, sunoId: t.sunoId })));

        clearTimeout(timeout);

      } catch (error) {
        console.error('‚ùå Failed to load tracks:', error);
        setError(`Unable to load tracks at the moment. Please try refreshing the page.`);

        // Track error in analytics
        analyticsService.trackError({
          type: 'track_loading_error',
          message: error instanceof Error ? error.message : 'Unknown error',
          stack: error instanceof Error ? error.stack : undefined,
          context: { tracksRequested: true }
        });

        // Track performance issue
        analyticsService.trackPerformanceIssue({
          type: 'error',
          details: 'Failed to load tracks from JSON file',
          metrics: {
            jsonLoadTime: performanceMonitor.jsonLoadTime,
            trackProcessingTime: performanceMonitor.trackProcessingTime,
            totalTime: performance.now() - performanceMonitor.startTime
          }
        });

        // Fallback to sample tracks
        console.log('üìã Falling back to sample tracks...');
        setAllTracks(SAMPLE_TRACKS);
        setTracks(SAMPLE_TRACKS);
        setLoadedCount(SAMPLE_TRACKS.length);
        setTotalTracks(SAMPLE_TRACKS.length);
      } finally {
        setLoading(false);
      }
    };

    loadTracks();

    // Cleanup handled where timeout is set
    return () => {};
  }, [loadTracksInBatches]);

  const moods = ['all', 'chill', 'epic', 'energetic', 'mysterious', 'uplifting', 'dark', 'peaceful'];
  const categories = ['all', ...getStreamingCategories()];
  const energyLevels = ['all', 'low', 'medium', 'high'];

  // Memoized filtered tracks for performance
  const filteredTracks = useMemo(() => {
    return tracks.filter(track => {
      const matchesSearch = track.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                           track.tags.some(tag => tag.toLowerCase().includes(searchTerm.toLowerCase())) ||
                           (track.artist && track.artist.toLowerCase().includes(searchTerm.toLowerCase()));
      const matchesMood = selectedMood === 'all' || track.mood === selectedMood;
      const matchesCategory = selectedCategory === 'all' || track.streamingCategory === selectedCategory;
      const matchesEnergy = selectedEnergy === 'all' || getEnergyLevel(track) === selectedEnergy;

      return matchesSearch && matchesMood && matchesCategory && matchesEnergy;
    });
  }, [searchTerm, selectedMood, selectedCategory, selectedEnergy, tracks]);

  // Virtualized tracks - only show visible range
  const visibleTracks = useMemo(() => {
    if (!virtualizationEnabled) {
      return filteredTracks;
    }

    const start = Math.max(0, visibleTrackRange.start);
    const end = Math.min(filteredTracks.length, visibleTrackRange.end);
    return filteredTracks.slice(start, end);
  }, [filteredTracks, virtualizationEnabled, visibleTrackRange]);

  const copyAttribution = () => {
    navigator.clipboard.writeText(
      "Music by Vivid Soundscapes ‚Äî free for use with credit"
    );
    alert("Attribution text copied!");
  };

  const formatCategoryName = (category: string) => {
    if (category === 'all') return 'All Categories';
    return getCategoryDisplayName(category);
  };

  const handleLoadMore = () => {
    const currentCount = tracks.length;
    const nextBatch = allTracks.slice(currentCount, currentCount + PERFORMANCE_CONFIG.LOAD_MORE_BATCH_SIZE);
    if (nextBatch.length > 0) {
      setTracks(prev => [...prev, ...nextBatch]);
      setLoadedCount(currentCount + nextBatch.length);

      // Track user interaction
      analyticsService.trackUserInteraction('load_more_tracks', {
        currentCount,
        newCount: currentCount + nextBatch.length,
        batchSize: nextBatch.length,
        totalAvailable: allTracks.length
      });
    }
  };

  const handleForceReload = () => {
    window.location.reload();
  };

  const logState = () => {
    console.log('Full State:', { tracks, loading, error, loadedCount, totalTracks, performanceMetrics });
  };

  const togglePerformanceMode = () => {
    const newMode = performanceMode === 'low' ? 'full' : 'low';
    setPerformanceMode(newMode);
    setVirtualizationEnabled(newMode === 'low');

    analyticsService.trackUserInteraction('toggle_performance_mode', {
      newMode,
      hardwareConcurrency: navigator.hardwareConcurrency,
      deviceMemory: (navigator as any).deviceMemory
    });
  };

  const handleTracksGenerated = (generatedTracks: StreamingTrack[]) => {
    console.log('üéµ AI-generated tracks received:', generatedTracks.length);

    // Add generated tracks to the existing library
    setTracks(prev => [...prev, ...generatedTracks]);
    setTotalTracks(prev => prev + generatedTracks.length);

    // Track analytics
    analyticsService.trackUserInteraction('ai_tracks_generated', {
      trackCount: generatedTracks.length,
      categories: generatedTracks.map(t => t.streamingCategory)
    });

    // Show success message
    alert(`Successfully generated ${generatedTracks.length} AI tracks! They have been added to your library.`);
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <p className="text-gray-400">Loading...</p>
          {loadedCount > 0 && (
            <p className="text-sm text-gray-500 mt-2">
              {loadedCount} of {totalTracks} tracks
            </p>
          )}
          {error && (
            <div className="mt-4 p-4 bg-red-900/50 border border-red-600 rounded-lg">
              <p className="text-red-300 text-sm">{error}</p>
            </div>
          )}
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="text-center">
        <h2 className="text-3xl font-bold text-white mb-2">Audio Library</h2>
        <p className="text-lg text-gray-300">{totalTracks} tracks available</p>

        {/* Demo Mode Banner */}
        {sunoService.isDemoMode() && (
          <div className="bg-blue-900/30 border border-blue-500/50 rounded-lg p-4 mt-4 max-w-2xl mx-auto">
            <div className="flex items-center justify-center space-x-3">
              <span className="text-blue-400 text-xl">üéµ</span>
              <div className="text-center">
                <p className="text-blue-200 font-medium">Demo Mode Active</p>
                <p className="text-blue-300 text-sm">
                  You're using sample tracks. To enable AI music generation, set your Suno.ai API key.
                </p>
                <a 
                  href="https://suno.ai/" 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="text-blue-400 hover:text-blue-300 underline text-sm mt-1 inline-block"
                >
                  Get API Key ‚Üí
                </a>
              </div>
            </div>
          </div>
        )}
        <div className="flex gap-2 mt-2 justify-center">
          {/* Removed YouTube Audio Library integration ‚Äî migrated to Suno API */}
          <button
            onClick={async () => {
              console.log('üß™ Testing Suno API connection...');
              try {
                // Test Suno API connection
                const response = await fetch('https://api.sunoapi.org/api/v1/generate', {
                  method: 'POST',
                  headers: {
                    'Authorization': `Bearer ${(import.meta as any).env?.VITE_SUNO_API_KEY || process.env.REACT_APP_SUNO_API_KEY}`,
                    'Content-Type': 'application/json'
                  },
                  body: JSON.stringify({
                    prompt: "Test connection - simple instrumental music",
                    customMode: false,
                    instrumental: true,
                    model: "V4"
                  })
                });

                const result = await response.json();
                console.log('üß™ Suno API test result:', result);
                alert(`Suno API test: ${result.code === 200 ? 'Success' : 'Failed'} - ${result.msg}`);
              } catch (error) {
                console.error('üß™ Suno API test error:', error);
                alert('Suno API test failed - check console for details');
              }
            }}
            className="px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg text-sm transition-colors"
          >
            üß™ Test Suno API
          </button>
          <button
            onClick={() => setShowSunoGenerator(!showSunoGenerator)}
            className="px-4 py-2 bg-purple-600 hover:bg-purple-700 text-white rounded-lg text-sm transition-colors"
          >
            {showSunoGenerator ? 'üéµ Hide AI Generator' : 'üéµ Show AI Generator'}
          </button>
          <button
            onClick={() => setShowAdminPanel(!showAdminPanel)}
            className="px-4 py-2 bg-red-600 hover:bg-red-700 text-white rounded-lg text-sm transition-colors"
          >
            {showAdminPanel ? 'üîí Hide Admin' : 'üîí Admin Panel'}
          </button>
          <button
            onClick={async () => {
              console.log('üîç Performance diagnostics button clicked');

              // Track performance test interaction
              analyticsService.trackUserInteraction('run_performance_test', {
                tracksLoaded: tracks.length,
                performanceMetrics: performanceMetrics
              });

              try {
                const analysis = await runPerformanceDiagnostics();
                console.log('üîç Performance analysis complete:', analysis);

                // Track performance test results
                if (analysis.recommendations.length > 0) {
                  analyticsService.trackPerformanceIssue({
                    type: 'slow_loading',
                    details: `Performance test completed with ${analysis.recommendations.length} recommendations`,
                    metrics: analysis.testResult
                  });
                }

                alert(`Performance test complete! Check console for details.\n\nRecommendations:\n${analysis.recommendations.join('\n')}`);
              } catch (error) {
                console.error('üîç Performance diagnostics error:', error);

                // Track performance test error
                analyticsService.trackError({
                  type: 'performance_test_error',
                  message: error instanceof Error ? error.message : 'Unknown error',
                  context: { tracksLoaded: tracks.length }
                });

                alert('Performance test failed. Check console for details.');
              }
            }}
            className="px-4 py-2 bg-green-600 hover:bg-green-700 text-white rounded-lg text-sm transition-colors"
          >
            üîç Performance Test
          </button>
          <button
            onClick={togglePerformanceMode}
            className={`px-4 py-2 rounded-lg text-sm transition-colors ${
              performanceMode === 'low' 
                ? 'bg-orange-600 hover:bg-orange-700 text-white' 
                : 'bg-purple-600 hover:bg-purple-700 text-white'
            }`}
          >
            {performanceMode === 'low' ? 'üöÄ Performance Mode' : '‚ö° Low-End Mode'}
          </button>
        </div>
      </div>

      {/* AI Music Generator */}
      {showSunoGenerator && (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          className="max-w-4xl mx-auto"
        >
          <SunoGenerator onTracksGenerated={handleTracksGenerated} />
        </motion.div>
      )}

      {/* Debug Info */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.2 }}
        className="max-w-6xl mx-auto p-4 bg-gray-800/50 border border-gray-600 rounded-lg"
      >
        <div className="flex items-center justify-between mb-2">
          <h3 className="text-lg font-semibold text-white">üîç Debug Information</h3>
          <button
            onClick={() => setShowDebugPanel(!showDebugPanel)}
            className="px-3 py-1 bg-gray-700 hover:bg-gray-600 text-white rounded text-sm transition-colors"
          >
            {showDebugPanel ? 'Hide' : 'Show'} Debug Panel
          </button>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 text-sm">
          <div>
            <span className="text-gray-400">Tracks Loaded:</span>
            <span className="text-white ml-2 font-mono">{loadedCount}</span>
          </div>
          <div>
            <span className="text-gray-400">Total Available:</span>
            <span className="text-white ml-2 font-mono">{totalTracks}</span>
          </div>
          <div>
            <span className="text-gray-400">API Key Status:</span>
            <span className="text-white ml-2 font-mono">{apiKeyStatus}</span>
          </div>
          <div>
            <span className="text-gray-400">Performance Mode:</span>
            <span className="text-white ml-2 font-mono">
              {performanceMode === 'low' ? '‚ö° Low-End' : performanceMode === 'full' ? 'üöÄ Full' : 'üîß Auto'}
            </span>
          </div>
        </div>
        {showDebugPanel && (
          <div className="mt-4 p-4 bg-gray-900/50 rounded-lg">
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-xs">
              <div>
                <span className="text-gray-400">Loading Speed:</span>
                <span className="text-white ml-2 font-mono">
                  {performanceMetrics.jsonLoadTime < 1000 ? 'Fast' : 
                   performanceMetrics.jsonLoadTime < 3000 ? 'Normal' : 'Slow'}
                </span>
              </div>
              <div>
                <span className="text-gray-400">Processing:</span>
                <span className="text-white ml-2 font-mono">
                  {performanceMetrics.trackProcessingTime < 1000 ? 'Quick' : 
                   performanceMetrics.trackProcessingTime < 3000 ? 'Standard' : 'Taking time'}
                </span>
              </div>
              <div>
                <span className="text-gray-400">Memory:</span>
                <span className="text-white ml-2 font-mono">
                  {performanceMetrics.memoryUsage < 50 * 1024 * 1024 ? 'Low' : 
                   performanceMetrics.memoryUsage < 100 * 1024 * 1024 ? 'Normal' : 'High'}
                </span>
              </div>
              <div>
                <span className="text-gray-400">Data Size:</span>
                <span className="text-white ml-2 font-mono">{performanceMetrics.fileSize}</span>
              </div>
            </div>
            {error && (
              <div className="mt-2 p-2 bg-red-900/50 border border-red-600 rounded text-red-300 text-xs">
                Issue: {error}
              </div>
            )}
          </div>
        )}
        <div className="mt-3 text-xs text-gray-400">
          <p>First 3 tracks: {tracks.slice(0, 3).map(t => `${t.title} (${t.sunoId || 'Manual Upload'})`).join(', ')}</p>
        </div>
      </motion.div>

      {/* Search and Filters */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.1 }}
        className="space-y-4 max-w-6xl mx-auto"
      >
        {/* Search Bar */}
        <div className="flex-1">
          <input
            type="text"
            placeholder="Search tracks by title, tags, or description..."
            onChange={(e) => debouncedSearch(e.target.value)}
            className="w-full px-4 py-3 bg-gray-800 border border-gray-600 rounded-2xl text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200"
          />
        </div>

        {/* Filter Controls */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
          {/* Mood Filter */}
          <select
            value={selectedMood}
            onChange={(e) => {
              setSelectedMood(e.target.value);
              analyticsService.trackUserInteraction('filter_mood', {
                selectedMood: e.target.value,
                tracksLoaded: tracks.length
              });
            }}
            className="w-full px-4 py-3 bg-gray-800 border border-gray-600 rounded-2xl text-white focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200"
          >
            {moods.map(mood => (
              <option key={mood} value={mood} className="bg-gray-800 text-white">
                {mood === 'all' ? 'All Moods' : mood.charAt(0).toUpperCase() + mood.slice(1)}
              </option>
            ))}
          </select>

          {/* Category Filter */}
          <select
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
            className="w-full px-4 py-3 bg-gray-800 border border-gray-600 rounded-2xl text-white focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200"
          >
            {categories.map(category => (
              <option key={category} value={category} className="bg-gray-800 text-white">
                {category === 'all' ? 'All Categories' : formatCategoryName(category)}
              </option>
            ))}
          </select>

          {/* Energy Level Filter */}
          <select
            value={selectedEnergy}
            onChange={(e) => setSelectedEnergy(e.target.value)}
            className="w-full px-4 py-3 bg-gray-800 border border-gray-600 rounded-2xl text-white focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200"
          >
            {energyLevels.map(energy => (
              <option key={energy} value={energy} className="bg-gray-800 text-white">
                {energy === 'all' ? 'All Energy Levels' : energy.charAt(0).toUpperCase() + energy.slice(1)}
              </option>
            ))}
          </select>

          {/* Results Count */}
          <div className="flex items-center justify-center px-4 py-3 bg-gray-800 border border-gray-600 rounded-2xl">
            <span className="text-white">
              {filteredTracks.length} track{filteredTracks.length !== 1 ? 's' : ''}
            </span>
          </div>
        </div>

        {/* Load More Button */}
        {loadedCount < totalTracks && (
          <div className="text-center">
            <button
              onClick={handleLoadMore}
              className="px-6 py-3 bg-green-600 hover:bg-green-700 text-white rounded-xl font-medium transition-all duration-200"
            >
              Load More Tracks ({loadedCount}/{totalTracks})
            </button>
          </div>
        )}

        {/* Agency Playlists */}
        <div className="space-y-4">
          <div className="flex items-center justify-between">
            <h3 className="text-lg font-semibold text-white">Agency Playlists</h3>
            <button
              onClick={() => setTracks(allTracks)}
              className="px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-xl font-medium transition-all duration-200 text-sm"
            >
              Show All Tracks
            </button>
          </div>
          {/* Removed YouTube Audio Library integration ‚Äî migrated to Suno API */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {getStreamingCategories().map((category) => (
              <div
                key={category}
                className="p-4 bg-gray-800 border border-gray-600 rounded-2xl hover:border-blue-500 transition-all duration-200 cursor-pointer"
                onClick={() => {
                  // Filter to show only tracks from this category
                  const categoryTracks = allTracks.filter(track => 
                    track.streamingCategory === category
                  );
                  setTracks(categoryTracks);
                }}
              >
                <h4 className="font-semibold text-white mb-2">{getCategoryDisplayName(category)}</h4>
                <p className="text-sm text-gray-400 mb-3">AI-generated tracks for {getCategoryDisplayName(category).toLowerCase()}</p>
                <div className="flex flex-wrap gap-2">
                  <span className="px-2 py-1 bg-blue-600 text-white text-xs rounded-full">
                    AI Generated
                  </span>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Quick Access Playlists */}
        <div className="space-y-4">
          <h3 className="text-lg font-semibold text-white">Quick Access</h3>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <div
              className="p-4 bg-gradient-to-br from-green-600/20 to-green-800/20 border border-green-600/30 rounded-2xl hover:border-green-500 transition-all duration-200 cursor-pointer"
              onClick={() => {
                // Filter by most popular tracks
                const popularTracks = allTracks.slice(0, 10); // For now, just show first 10
                setTracks(popularTracks);
              }}
            >
              <div className="flex items-center gap-2 mb-2">
                <svg className="w-5 h-5 text-green-400" fill="currentColor" viewBox="0 0 20 20">
                  <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
                </svg>
                <span className="font-semibold text-white">Most Popular</span>
              </div>
              <p className="text-sm text-green-400">Frequently used tracks</p>
            </div>

            <div
              className="p-4 bg-gradient-to-br from-blue-600/20 to-blue-800/20 border border-blue-600/30 rounded-2xl hover:border-blue-500 transition-all duration-200 cursor-pointer"
              onClick={() => {
                // Filter by energetic tracks
                const energeticTracks = allTracks.filter(track => (track.energy || 3) >= 4);
                setTracks(energeticTracks);
              }}
            >
              <div className="flex items-center gap-2 mb-2">
                <svg className="w-5 h-5 text-blue-400" fill="currentColor" viewBox="0 0 20 20">
                  <path fillRule="evenodd" d="M11.3 1.046A1 1 0 0112 2v5h4a1 1 0 01.82 1.573l-7 10A1 1 0 018 18v-5H4a1 1 0 01-.82-1.573l7-10a1 1 0 011.12-.38z" clipRule="evenodd" />
                </svg>
                <span className="font-semibold text-white">High Energy</span>
              </div>
              <p className="text-sm text-blue-400">Hype and action music</p>
            </div>

            <div
              className="p-4 bg-gradient-to-br from-purple-600/20 to-purple-800/20 border border-purple-600/30 rounded-2xl hover:border-purple-500 transition-all duration-200 cursor-pointer"
              onClick={() => {
                // Filter by chill tracks
                const chillTracks = allTracks.filter(track => (track.energy || 3) <= 2);
                setTracks(chillTracks);
              }}
            >
              <div className="flex items-center gap-2 mb-2">
                <svg className="w-5 h-5 text-purple-400" fill="currentColor" viewBox="0 0 20 20">
                  <path fillRule="evenodd" d="M3 4a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zm0 4a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zm0 4a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1z" clipRule="evenodd" />
                </svg>
                <span className="font-semibold text-white">Chill Vibes</span>
              </div>
              <p className="text-sm text-purple-400">Relaxed background music</p>
            </div>

            <div
              className="p-4 bg-gradient-to-br from-orange-600/20 to-orange-800/20 border border-orange-600/30 rounded-2xl hover:border-orange-500 transition-all duration-200 cursor-pointer"
              onClick={() => {
                // Filter by gaming tracks
                const gamingTracks = allTracks.filter(track => 
                  (track.category === 'gaming' || track.streamingCategory?.includes('gaming') || 
                  track.streamingCategory?.includes('action'))
                );
                setTracks(gamingTracks);
              }}
            >
              <div className="flex items-center gap-2 mb-2">
                <svg className="w-5 h-5 text-orange-400" fill="currentColor" viewBox="0 0 20 20">
                  <path d="M13 6a3 3 0 11-6 0 3 3 0 016 0zM18 8a2 2 0 11-4 0 2 2 0 014 0zM14 15a4 4 0 00-8 0v3h8v-3z" />
                </svg>
                <span className="font-semibold text-white">Gaming</span>
              </div>
              <p className="text-sm text-orange-400">Perfect for gameplay</p>
            </div>
          </div>
        </div>
      </motion.div>

      {/* Attribution Copy Button */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.15 }}
        className="text-center"
      >
        <button
          onClick={copyAttribution}
          className="px-6 py-3 bg-blue-600 hover:bg-blue-700 text-white rounded-2xl font-medium transition-all duration-200 shadow-lg hover:shadow-xl"
        >
          Copy Attribution Text
        </button>
      </motion.div>

      {/* Track Grid */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.2 }}
        className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6"
      >
        {visibleTracks.map((track, index) => (
          <div key={track.id} className="track-card">
            {performanceMode === 'low' ? (
              <UltraLightTrackCard 
                track={track} 
                onPlay={() => {
                  setCurrentPlayingTrack(track);
                  setIsAudioPlaying(true);
                }}
                onPause={() => {
                  setIsAudioPlaying(false);
                }}
                isPlaying={currentPlayingTrack?.id === track.id && isAudioPlaying}
              />
            ) : (
              <TrackCard track={track} />
            )}
          </div>
        ))}

        {/* Removed YouTube Audio Library integration ‚Äî migrated to Suno API */}
        {/* Audio player will be implemented for Suno API tracks */}
      </motion.div>

      {filteredTracks.length === 0 && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          className="text-center py-12"
        >
          <p className="text-xl text-gray-400">No tracks found matching your criteria.</p>
          <p className="text-gray-500 mt-2">Try adjusting your filters or search terms.</p>
        </motion.div>
      )}

      {/* Recently Generated Tracks */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.2 }}
        className="mb-8"
      >
        <RecentlyGeneratedTracks />
      </motion.div>

      {/* Footer */}
      <motion.footer
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.3 }}
        className="text-center py-8"
      >
        <p className="text-gray-400">
          Created by <span className="text-blue-500 font-medium">Vivid Soundscapes</span>
        </p>
      </motion.footer>

      {/* Debug Panel - Only show in development */}
      {process.env.NODE_ENV === 'development' && (
        <div 
          className="debug-panel" 
          style={{
            position: 'fixed', 
            bottom: 0, 
            left: 0, 
            background: 'rgba(0,0,0,0.8)', 
            color: 'white', 
            padding: '10px', 
            fontSize: '12px',
            zIndex: 1000,
            borderTop: '1px solid #333',
            borderRight: '1px solid #333'
          }}
        >
          <div>Tracks: {loadedCount}/{totalTracks}</div>
          <div>Status: {loading ? 'Loading...' : 'Ready'}</div>
          <div>Memory: {((performance as any).memory?.usedJSHeapSize / 1024 / 1024).toFixed(1)}MB</div>
          <div>Speed: {performanceMetrics.jsonLoadTime < 1000 ? 'Fast' : performanceMetrics.jsonLoadTime < 3000 ? 'Normal' : 'Slow'}</div>
          <div>Processing: {performanceMetrics.trackProcessingTime < 1000 ? 'Quick' : performanceMetrics.trackProcessingTime < 3000 ? 'Standard' : 'Slow'}</div>
          <div>Mode: {performanceMode}</div>
          <button 
            onClick={handleForceReload}
            style={{
              background: '#dc2626',
              color: 'white',
              border: 'none',
              padding: '4px 8px',
              borderRadius: '4px',
              fontSize: '10px',
              marginTop: '4px',
              cursor: 'pointer'
            }}
          >
            Force Reload
          </button>
          <button 
            onClick={logState}
            style={{
              background: '#059669',
              color: 'white',
              border: 'none',
              padding: '4px 8px',
              borderRadius: '4px',
              fontSize: '10px',
              marginTop: '4px',
              marginLeft: '4px',
              cursor: 'pointer'
            }}
          >
            Log State
          </button>
          <button 
            onClick={() => webhookService.triggerTestWebhooks()}
            style={{
              background: '#7c3aed',
              color: 'white',
              border: 'none',
              padding: '4px 8px',
              borderRadius: '4px',
              fontSize: '10px',
              marginTop: '4px',
              marginLeft: '4px',
              cursor: 'pointer'
            }}
          >
            Test Webhooks
          </button>
        </div>
      )}

      {/* Analytics Dashboard - Development Only */}
      <AnalyticsDashboard />

      {/* Admin Panel */}
      {showAdminPanel && (
        <AdminPanel onClose={() => setShowAdminPanel(false)} />
      )}
    </div>
  );
};

export default StreamSoundboard;
