import { useState, useMemo, useEffect, useCallback } from 'react';
import { motion } from 'framer-motion';
import { StreamingTrack } from '../types/track';
// Removed YouTube Audio Library integration â€” migrated to Suno API
import { getStreamingCategories, getCategoryDisplayName } from '../services/sunoService';
import sunoService from '../services/sunoService';
import { runPerformanceDiagnostics } from '../utils/performanceTest';
import analyticsService from '../services/analyticsService';
import webhookService from '../services/webhookService';
import AdminPanel from './admin/AdminPanel';
import { getEnergyLevel } from '../types/track';
import AnalyticsDashboard from './AnalyticsDashboard';
import TrackCard from './TrackCard';
// import LightweightTrackCard from './LightweightTrackCard';
import UltraLightTrackCard from './UltraLightTrackCard';
// Removed YouTube Audio Library integration â€” migrated to Suno API
import SunoGenerator from './SunoGenerator';
import RecentlyGeneratedTracks from './RecentlyGeneratedTracks';
import authService, { FeatureFlags } from '../services/authService';

// Performance monitoring utilities
const performanceMonitor = {
  startTime: 0,
  jsonLoadTime: 0,
  trackProcessingTime: 0,
  memoryUsage: 0,

  start() {
    this.startTime = performance.now();
    console.time('Total Load Time');
    console.time('JSON Load Time');
    console.log('Available Memory:', (performance as any).memory?.usedJSHeapSize);
    console.log('JSON File Size Check');
  },

  endJsonLoad() {
    console.timeEnd('JSON Load Time');
    this.jsonLoadTime = performance.now() - this.startTime;
    console.time('Track Processing Time');
  },

  endTrackProcessing() {
    console.timeEnd('Track Processing Time');
    this.trackProcessingTime = performance.now() - this.startTime;
    console.timeEnd('Total Load Time');
    this.memoryUsage = (performance as any).memory?.usedJSHeapSize || 0;
    console.log('Final Memory Usage:', this.memoryUsage);
  }
};

// Performance configuration for older hardware
const PERFORMANCE_CONFIG = {
  INITIAL_BATCH_SIZE: 3, // Very small initial load for 2013 MacBook Pro
  LOAD_MORE_BATCH_SIZE: 5, // Tiny batches
  VIRTUALIZATION_WINDOW_SIZE: 2, // Only 2 visible items
  LAZY_LOAD_DELAY: 500, // Much longer delay between batches
  PERFORMANCE_MODE_THRESHOLD: 1, // Very low threshold
};

// Removed YouTube Audio Library integration â€” migrated to Suno API
// Sample tracks for fallback (Suno API generated)
const SAMPLE_TRACKS: StreamingTrack[] = [
  {
    id: 'sample_1',
    title: 'AI Generated Chill Track',
    artist: 'AI-generated sample track for testing',
    duration: 150,
    audioUrl: 'https://api.sunoapi.org/sample1.mp3',
    category: 'gaming',
    subcategory: 'chill',
    mood: 'chill',
    energy: 2,
    tags: ['ambient', 'chill', 'chill_gaming', 'low'],
    streamSafe: true,
    loopFriendly: true,
    hasIntro: false,
    hasOutro: false,
    uploadDate: new Date().toISOString(),
    uploadedBy: 'system',
    approved: true,
    // Legacy fields for backward compatibility
    sunoId: 'sample1',
    streamingCategory: 'chill_gaming',
    energyLevel: 'low',
    genre: 'ambient',
    dmcaSafe: true,
    vodSafe: true,
    license: 'royalty_free',
    licenseDetails: 'Suno AI generated track',
    platformCompliance: {
      twitch: 'safe',
      youtube: 'safe',
      facebook: 'safe',
      tiktok: 'safe'
    },
    usageTracking: {
      usageCount: 0,
      lastUsed: undefined
    },
    agencyNotes: 'AI-generated sample track'
  },
  {
    id: 'sample_2',
    title: 'AI Generated Energetic Track',
    artist: 'AI-generated energetic track for testing',
    duration: 195,
    audioUrl: 'https://api.sunoapi.org/sample2.mp3',
    category: 'gaming',
    subcategory: 'action',
    mood: 'energetic',
    energy: 4,
    tags: ['electronic', 'energetic', 'gaming_action', 'high'],
    streamSafe: true,
    loopFriendly: true,
    hasIntro: false,
    hasOutro: false,
    uploadDate: new Date().toISOString(),
    uploadedBy: 'system',
    approved: true,
    // Legacy fields for backward compatibility
    sunoId: 'sample2',
    streamingCategory: 'gaming_action',
    energyLevel: 'high',
    genre: 'electronic',
    dmcaSafe: true,
    vodSafe: true,
    license: 'royalty_free',
    licenseDetails: 'Suno AI generated track',
    platformCompliance: {
      twitch: 'safe',
      youtube: 'safe',
      facebook: 'safe',
      tiktok: 'safe'
    },
    usageTracking: {
      usageCount: 0,
      lastUsed: undefined
    },
    agencyNotes: 'Sample track'
  }
];

const StreamSoundboard: React.FC = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedMood, setSelectedMood] = useState<string>('all');
  const [selectedCategory, setSelectedCategory] = useState<string>('all');
  const [selectedEnergy, setSelectedEnergy] = useState<string>('all');
  const [tracks, setTracks] = useState<StreamingTrack[]>([]);
  const [allTracks, setAllTracks] = useState<StreamingTrack[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [loadedCount, setLoadedCount] = useState(0);
  const [totalTracks, setTotalTracks] = useState(0);
  const [showDebugPanel, setShowDebugPanel] = useState(false);

  // Performance mode state
  const [performanceMode, setPerformanceMode] = useState<'auto' | 'low' | 'full'>('auto');
  const [virtualizationEnabled, setVirtualizationEnabled] = useState(false);
  const [visibleTrackRange] = useState({ start: 0, end: 5 });

  // Shared audio state
  const [currentPlayingTrack, setCurrentPlayingTrack] = useState<StreamingTrack | null>(null);
  const [isAudioPlaying, setIsAudioPlaying] = useState(false);
  const [showSunoGenerator, setShowSunoGenerator] = useState(false);
  const [showAdminPanel, setShowAdminPanel] = useState(false);

  // Performance state
  const [performanceMetrics, setPerformanceMetrics] = useState({
    jsonLoadTime: 0,
    trackProcessingTime: 0,
    memoryUsage: 0,
    fileSize: '0 KB'
  });

  // Check API key status safely
  const apiKeyStatus = process.env.REACT_APP_YOUTUBE_API_KEY ? 'âœ… Configured' : 'âŒ Missing';

  // Get feature flags for current user
  const [featureFlags, setFeatureFlags] = useState<FeatureFlags | null>(null);

  // Load feature flags for current user
  useEffect(() => {
    const flags = authService.getFeatureFlags();
    setFeatureFlags(flags);
    console.log('ðŸ”’ Feature flags loaded:', flags);
  }, []);

  // Detect hardware capabilities and set performance mode
  useEffect(() => {
    const hardwareConcurrency = navigator.hardwareConcurrency || 1;
    const deviceMemory = (navigator as any).deviceMemory || 4;

    // Force low performance mode for 2013 MacBook Pro
    if (performanceMode === 'auto') {
      // 2013 MacBook Pro typically has 2-4 cores, so we'll force low mode
      setPerformanceMode('low');
      setVirtualizationEnabled(true);
      console.log('ðŸ”§ Forcing low performance mode for 2013 MacBook Pro');
      console.log(`ðŸ”§ Hardware: ${hardwareConcurrency} cores, ${deviceMemory}GB RAM`);
    }
  }, [performanceMode]);

  // Debounced search function for performance
  const debouncedSearch = useCallback((value: string) => {
    // simple 300ms debounce per call
    window.clearTimeout((debouncedSearch as any)._t);
    (debouncedSearch as any)._t = window.setTimeout(() => {
      setSearchTerm(value);
      if (value.length > 0) {
        analyticsService.trackUserInteraction('search_tracks', {
          searchTerm: value,
          searchLength: value.length,
          tracksLoaded: tracks.length
        });
      }
    }, 300);
  }, [tracks.length]);

  // Memory-safe loading with batching - optimized for older hardware
  const loadTracksInBatches = useCallback(async (allTracksData: StreamingTrack[], batchSize: number = PERFORMANCE_CONFIG.INITIAL_BATCH_SIZE) => {
    const total = allTracksData.length;
    setTotalTracks(total);

    // Load first batch immediately
    const firstBatch = allTracksData.slice(0, batchSize);
    setTracks(firstBatch);
    setLoadedCount(batchSize);

    // Set up remaining batches
    const remainingTracks = allTracksData.slice(batchSize);
    const batches = [];

    for (let i = 0; i < remainingTracks.length; i += PERFORMANCE_CONFIG.LOAD_MORE_BATCH_SIZE) {
      batches.push(remainingTracks.slice(i, i + PERFORMANCE_CONFIG.LOAD_MORE_BATCH_SIZE));
    }

    // Load remaining batches with delay to prevent blocking
    for (let i = 0; i < batches.length; i++) {
      await new Promise(resolve => setTimeout(resolve, PERFORMANCE_CONFIG.LAZY_LOAD_DELAY));
      const newBatch = batches[i];
      setTracks(prev => [...prev, ...newBatch]);
      setLoadedCount(prev => prev + newBatch.length);
    }
  }, []);

  // Load curated tracks from YouTube service with performance monitoring
  useEffect(() => {
    const loadTracks = async () => {
      try {
        performanceMonitor.start();

        // Set timeout for loading
        const timeout = setTimeout(() => {
          setError('Loading is taking longer than expected. This might be due to your device or connection. Try refreshing the page.');

          // Track timeout in analytics
          analyticsService.trackPerformanceIssue({
            type: 'timeout',
            details: 'App loading exceeded 10 second timeout',
            metrics: {
              timeoutDuration: 10000,
              jsonLoadTime: performanceMonitor.jsonLoadTime,
              trackProcessingTime: performanceMonitor.trackProcessingTime,
              totalTime: performance.now() - performanceMonitor.startTime
            }
          });

          setLoading(false);
        }, 10000);


        // Removed YouTube Audio Library integration â€” migrated to Suno API
        console.log('ðŸ” Loading tracks from Suno API...');

        performanceMonitor.endJsonLoad();

        // For now, use sample tracks until Suno API integration is complete
        let tracksData = SAMPLE_TRACKS;

        // Apply plan-based track limits
        if (featureFlags && featureFlags.maxTracks > 0) {
          const maxTracks = featureFlags.maxTracks;
          if (maxTracks !== -1 && tracksData.length > maxTracks) {
            tracksData = tracksData.slice(0, maxTracks);
            console.log(`ðŸ”’ Plan limit applied: showing ${maxTracks} tracks out of ${SAMPLE_TRACKS.length} total`);
          }
        }

        performanceMonitor.endTrackProcessing();

        // Update performance metrics
        const finalMetrics = {
          jsonLoadTime: performanceMonitor.jsonLoadTime,
          trackProcessingTime: performanceMonitor.trackProcessingTime,
          memoryUsage: performanceMonitor.memoryUsage,
          fileSize: '304 KB' // Known file size
        };

        setPerformanceMetrics(finalMetrics);

        setAllTracks(tracksData);

        // Load tracks in batches for better performance
        await loadTracksInBatches(tracksData);

        // Track successful app load in analytics
        analyticsService.trackAppLoad({
          jsonLoadTime: finalMetrics.jsonLoadTime,
          trackProcessingTime: finalMetrics.trackProcessingTime,
          totalLoadTime: performance.now() - performanceMonitor.startTime,
          tracksLoaded: tracksData.length,
          totalTracks: tracksData.length,
          loadSuccess: true
        });

        console.log(`âœ… Loaded ${tracksData.length} tracks`);
        console.log('ðŸ” First few tracks loaded:', tracksData.slice(0, 3));
        console.log('ðŸ” Track sources:', tracksData.map(t => ({ id: t.id, title: t.title, sunoId: t.sunoId })));

        clearTimeout(timeout);

      } catch (error) {
        console.error('âŒ Failed to load tracks:', error);
        setError(`Unable to load tracks at the moment. Please try refreshing the page.`);

        // Track error in analytics
        analyticsService.trackError({
          type: 'track_loading_error',
          message: error instanceof Error ? error.message : 'Unknown error',
          stack: error instanceof Error ? error.stack : undefined,
          context: { tracksRequested: true }
        });

        // Track performance issue
        analyticsService.trackPerformanceIssue({
          type: 'error',
          details: 'Failed to load tracks from JSON file',
          metrics: {
            jsonLoadTime: performanceMonitor.jsonLoadTime,
            trackProcessingTime: performanceMonitor.trackProcessingTime,
            totalTime: performance.now() - performanceMonitor.startTime
          }
        });

        // Fallback to sample tracks
        console.log('ðŸ“‹ Falling back to sample tracks...');
        setAllTracks(SAMPLE_TRACKS);
        setTracks(SAMPLE_TRACKS);
        setLoadedCount(SAMPLE_TRACKS.length);
        setTotalTracks(SAMPLE_TRACKS.length);
      } finally {
        setLoading(false);
      }
    };

    loadTracks();

    // Cleanup handled where timeout is set
    return () => {};
  }, [loadTracksInBatches, featureFlags]);

