import { useState, memo, useEffect } from 'react';
import { motion } from 'framer-motion';
import { StreamingTrack, getEnergyLevel } from '../types/track';
import SunoPlayer from './SunoPlayer';
import DMCAComplianceIndicator from './DMCAComplianceIndicator';
import unifiedAudioController from '../services/unifiedAudioController';

interface TrackCardProps {
  track: StreamingTrack;
}

const TrackCard: React.FC<TrackCardProps> = memo(({ track }) => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);
  const [showCopied, setShowCopied] = useState(false);
  const [volume, setVolume] = useState(50);

  // Register with unified audio controller
  useEffect(() => {
    const stopCallback = () => setIsPlaying(false);
    unifiedAudioController.registerStopListener(track.id, stopCallback);

    return () => {
      unifiedAudioController.unregisterStopListener(track.id);
    };
  }, [track.id]);

  // const handlePlayPause = () => {
  //   if (isPlaying) {
  //     setIsPlaying(false);
  //     sunoAudioController.stop();
  //   } else {
  //     setIsPlaying(true);
  //     sunoAudioController.playTrack(track).catch(console.error);
  //   }
  // };

  const handlePlay = () => {
    setIsPlaying(true);
    unifiedAudioController.playTrack(track).catch(console.error);
  };

  const handlePause = () => {
    setIsPlaying(false);
    unifiedAudioController.pause();
  };

  const handleEnd = () => {
    setIsPlaying(false);
    unifiedAudioController.stop();
  };

  const handleStreamReady = async () => {
    setIsProcessing(true);
    try {
      // For professional streaming, we keep users within the app
      console.log(`Track ready for streaming: ${track.title}`);
      // Simulate processing delay
      await new Promise(resolve => setTimeout(resolve, 500));
    } catch (error) {
      console.error('Processing failed:', error);
    } finally {
      setIsProcessing(false);
    }
  };

  const handleCopyAttribution = async () => {
    const attributionText = `"Music by Vivid Soundscapes — free for use with credit"`;
    try {
      await navigator.clipboard.writeText(attributionText);
      setShowCopied(true);
      setTimeout(() => setShowCopied(false), 2000);
    } catch (error) {
      console.error('Failed to copy:', error);
    }
  };

  const getEnergyLevelColor = (level: string) => {
    switch (level) {
      case 'low': return 'bg-green-600/20 text-green-400 border-green-600/30';
      case 'medium': return 'bg-yellow-600/20 text-yellow-400 border-yellow-600/30';
      case 'high': return 'bg-red-600/20 text-red-400 border-red-600/30';
      default: return 'bg-gray-600/20 text-gray-400 border-gray-600/30';
    }
  };

  const getStreamingCategoryColor = (category: string) => {
    switch (category) {
      case 'intro': return 'bg-purple-600/20 text-purple-400 border-purple-600/30';
      case 'background_chat': return 'bg-blue-600/20 text-blue-400 border-blue-600/30';
      case 'gaming_action': return 'bg-orange-600/20 text-orange-400 border-orange-600/30';
      case 'boss_battle': return 'bg-red-600/20 text-red-400 border-red-600/30';
      case 'intermission': return 'bg-indigo-600/20 text-indigo-400 border-indigo-600/30';
      case 'outro': return 'bg-pink-600/20 text-pink-400 border-pink-600/30';
      default: return 'bg-gray-600/20 text-gray-400 border-gray-600/30';
    }
  };

  const formatStreamingCategory = (category: string) => {
    return category.replace('_', ' ').replace(/\b\w/g, l => l.toUpperCase());
  };

  return (
    <motion.div
      className="bg-gray-800 rounded-2xl shadow-lg hover:shadow-xl transition-all duration-300 border border-gray-600 p-6 space-y-4"
      whileHover={{ y: -5, scale: 1.02 }}
      transition={{ duration: 0.2 }}
    >
      {/* Removed YouTube Audio Library integration — migrated to Suno API */}
      {/* Audio player will be implemented for Suno API tracks */}

      {/* Track Title */}
      <h3 className="text-xl font-bold text-white">{track.title}</h3>

      {/* Duration and Energy Level */}
      <div className="flex items-center justify-between">
        <span className="text-sm text-gray-400">Duration: {track.duration}</span>
                  <span className={`px-2 py-1 text-xs rounded-full border ${getEnergyLevelColor(getEnergyLevel(track))}`}>
            {getEnergyLevel(track).toUpperCase()} ENERGY
          </span>
      </div>

      {/* Streaming Category */}
      <div className="flex justify-center">
                  <span className={`px-3 py-1 text-sm rounded-full border ${getStreamingCategoryColor(track.streamingCategory || '')}`}>
            {formatStreamingCategory(track.streamingCategory || '')}
          </span>
      </div>

      {/* Mood and Genre */}
      <div className="flex flex-wrap gap-2">
        <span className="px-3 py-1 bg-blue-600/20 text-blue-400 text-sm rounded-full border border-blue-600/30">
          {track.mood}
        </span>
        <span className="px-3 py-1 bg-green-600/20 text-green-400 text-sm rounded-full border border-green-600/30">
          {track.genre}
        </span>
      </div>

      {/* Tags */}
      <div className="flex flex-wrap gap-2">
        {track.tags.slice(0, 3).map((tag, index) => (
          <span
            key={index}
            className="px-2 py-1 bg-gray-700/50 text-gray-300 text-xs rounded-full border border-gray-600/30"
          >
            {tag}
          </span>
        ))}
        {track.tags.length > 3 && (
          <span className="px-2 py-1 bg-gray-700/50 text-gray-400 text-xs rounded-full border border-gray-600/30">
            +{track.tags.length - 3} more
          </span>
        )}
      </div>

      {/* Description */}
              {track.artist && (
          <p className="text-sm text-gray-400 line-clamp-2">Artist: {track.artist}</p>
        )}

      {/* Audio Controls */}
      <div className="space-y-3">
        {/* Loop Controls */}
        <div className="flex items-center justify-between">
          <span className="text-sm text-gray-400">Loop</span>
          <div className="flex items-center space-x-2">
            <button
              onClick={() => unifiedAudioController.setLooping(true, -1)}
              className="px-3 py-1 bg-blue-600 hover:bg-blue-700 text-white text-xs rounded-lg transition-colors"
            >
              ∞
            </button>
            <button
              onClick={() => unifiedAudioController.setLooping(false, 1)}
              className="px-3 py-1 bg-gray-600 hover:bg-gray-700 text-white text-xs rounded-lg transition-colors"
            >
              Off
            </button>
          </div>
        </div>

        {/* Fade Effects */}
        <div className="flex items-center justify-between">
          <span className="text-sm text-gray-400">Fade Effects</span>
          <div className="flex items-center space-x-2">
            <button
              onClick={() => unifiedAudioController.fadeIn(2)}
              className="px-3 py-1 bg-green-600 hover:bg-green-700 text-white text-xs rounded-lg transition-colors"
            >
              Fade In
            </button>
            <button
              onClick={() => unifiedAudioController.fadeOut(2)}
              className="px-3 py-1 bg-red-600 hover:bg-red-700 text-white text-xs rounded-lg transition-colors"
            >
              Fade Out
            </button>
          </div>
        </div>

        {/* Crossfade Play */}
        <button
          onClick={() => unifiedAudioController.playTrack(track, true)}
          className="w-full px-4 py-2 bg-purple-600 hover:bg-purple-700 text-white text-sm rounded-lg transition-colors"
        >
          Play with Crossfade
        </button>
      </div>

      {/* Suno Audio Player */}
      <SunoPlayer
        track={track}
        isPlaying={isPlaying}
        onPlay={handlePlay}
        onPause={handlePause}
        onEnd={handleEnd}
        volume={volume}
        onVolumeChange={setVolume}
      />

      {/* Stream Ready Status */}
      <div className="flex items-center justify-between">
        <span className="text-sm text-gray-400">Stream Status</span>
        <button
          onClick={handleStreamReady}
          disabled={isProcessing}
          className={`px-3 py-1 rounded-lg text-xs font-medium transition-all duration-200 ${
            isProcessing
              ? 'bg-yellow-600 text-white'
              : 'bg-green-600 hover:bg-green-700 text-white'
          }`}
        >
          {isProcessing ? 'Processing...' : 'Ready'}
        </button>
      </div>

      {/* DMCA Compliance Indicator */}
      <div className="border-t border-gray-700 pt-4">
        <div className="flex items-center justify-between">
          <span className="text-xs text-gray-400">DMCA Status</span>
          <DMCAComplianceIndicator 
            track={track} 
            compact={true}
            onTrackUsage={(trackId) => {
              console.log(`Track usage tracked: ${trackId}`);
              // In production, this would send data to your analytics system
            }}
          />
        </div>
      </div>

      {/* Copy Attribution Button */}
      <button
        onClick={handleCopyAttribution}
        className="w-full bg-gray-700 hover:bg-gray-600 text-white font-medium py-2 px-4 rounded-2xl transition-all duration-200 shadow-lg hover:shadow-xl flex items-center justify-center gap-2"
      >
        {showCopied ? (
          <>
            <svg className="w-5 h-5 text-green-400" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
            </svg>
            Copied!
          </>
        ) : (
          <>
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 16H6a2 2 0 01-2-2V6a2 2 0 012-2h8a2 2 0 012 2v2m-6 12h8a2 2 0 002-2v-8a2 2 0 00-2-2h-8a2 2 0 00-2 2v8a2 2 0 002 2z" />
            </svg>
            Copy Attribution
          </>
        )}
      </button>
    </motion.div>
  );
});

export default TrackCard;
