  },
  {
    id: 'intro-outro',
    name: 'Intro/Outro',
    description: 'Short tracks for stream beginnings and endings',
    color: '#45b7d1',
    icon: 'ðŸŽ¬',
    subcategories: ['energetic-intro', 'calm-outro', 'branded-stinger', 'transition', 'countdown', 'welcome', 'goodbye']
  },
  {
    id: 'energy',
    name: 'Hype/Energy',
    description: 'High-energy music for exciting moments',
    color: '#f9ca24',
    icon: 'âš¡',
    subcategories: ['hype', 'celebration', 'pump-up', 'intense', 'dramatic', 'epic', 'victory', 'breakthrough']
  },
  {
    id: 'ambient',
    name: 'Ambient',
    description: 'Background atmosphere and mood music',
    color: '#6c5ce7',
    icon: 'ðŸŒ™',
    subcategories: ['space', 'nature', 'minimal', 'meditation', 'atmospheric', 'dreamy', 'mysterious', 'peaceful']
  }
];

export const getCategoryById = (id: string): Category | undefined => {
  return STREAMING_CATEGORIES.find(cat => cat.id === id);
};

export const getSubcategoriesByCategory = (categoryId: string): string[] => {
  const category = getCategoryById(categoryId);
  return category?.subcategories || [];
};

export const getCategoryDisplayName = (categoryId: string): string => {
  const category = getCategoryById(categoryId);
  return category?.name || categoryId;
};

export const getCategoryColor = (categoryId: string): string => {
  const category = getCategoryById(categoryId);
  return category?.color || '#6b7280';
};

export const getCategoryIcon = (categoryId: string): string => {
  const category = getCategoryById(categoryId);
  return category?.icon || 'ðŸŽµ';
};

// Legacy categories for backward compatibility
export const LEGACY_CATEGORIES = {
  STREAM_STARTING_SOON: 'stream_starting_soon',
  CHILL_GAMING: 'chill_gaming',
  GAMING_ACTION: 'gaming_action',
  HYPE_RAID: 'hype_raid',
  BREAK_BRB: 'break_brb',
  TALK_SHOW: 'talk_show',
  INTRO_OUTRO: 'intro_outro',
  BOSS_BATTLE: 'boss_battle',
  INTERMISSION: 'intermission',
  BACKGROUND_CHAT: 'background_chat'
} as const;

// Migration helper function
export const migrateLegacyCategory = (legacyCategory: string): string => {
  const migrationMap: { [key: string]: string } = {
    'stream_starting_soon': 'intro-outro',
    'chill_gaming': 'gaming',
    'gaming_action': 'gaming',
    'hype_raid': 'energy',
    'break_brb': 'ambient',
    'talk_show': 'chat',
    'intro_outro': 'intro-outro',
    'boss_battle': 'gaming',
    'intermission': 'ambient',
    'background_chat': 'chat'
  };

  return migrationMap[legacyCategory] || 'gaming';
};
