import React, { useState, useEffect, useRef, useCallback } from 'react';
import { motion } from 'framer-motion';
import { StreamingTrack, getStreamingCategory, getEnergyLevel } from '../types/track';

interface SunoPlayerProps {
  track: StreamingTrack;
  isPlaying: boolean;
  onPlay: () => void;
  onPause: () => void;
  onEnd: () => void;
  volume: number;
  onVolumeChange?: (volume: number) => void;
}

const SunoPlayer: React.FC<SunoPlayerProps> = ({
  track,
  isPlaying,
  onPlay,
  onPause,
  onEnd,
  volume,
  onVolumeChange
}) => {
  const audioRef = useRef<HTMLAudioElement>(null);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Handle audio loading
  useEffect(() => {
    if (!track.audioUrl) {
      setError('No audio URL available for this track');
      return;
    }

    setError(null);
    setIsLoading(true);

    if (audioRef.current) {
      audioRef.current.src = track.audioUrl;
      audioRef.current.load();
    }
  }, [track.audioUrl]);

  // Handle play/pause
  useEffect(() => {
    if (!audioRef.current) return;

    if (isPlaying) {
      audioRef.current.play().catch((err) => {
        console.error('Failed to play audio:', err);
        setError('Failed to play audio');
        onPause();
      });
    } else {
      audioRef.current.pause();
    }
  }, [isPlaying, onPause]);

  // Handle volume changes
  useEffect(() => {
    if (audioRef.current) {
      audioRef.current.volume = volume / 100;
    }
  }, [volume]);

  // Audio event handlers
  const handleLoadedMetadata = useCallback(() => {
    if (audioRef.current) {
      setDuration(audioRef.current.duration);
      setIsLoading(false);
    }
  }, []);

  const handleTimeUpdate = useCallback(() => {
    if (audioRef.current) {
      setCurrentTime(audioRef.current.currentTime);
    }
  }, []);

  const handleEnded = useCallback(() => {
    setCurrentTime(0);
    onEnd();
  }, [onEnd]);

  const handleError = useCallback((e: Event) => {
    console.error('Audio error:', e);
    setError('Failed to load audio');
    setIsLoading(false);
    onPause();
  }, [onPause]);

  // Progress bar handlers
  const handleProgressClick = useCallback((e: React.MouseEvent<HTMLDivElement>) => {
    if (!audioRef.current || !duration) return;

    const rect = e.currentTarget.getBoundingClientRect();
    const clickX = e.clientX - rect.left;
    const clickPercent = clickX / rect.width;
    const newTime = clickPercent * duration;

    audioRef.current.currentTime = newTime;
    setCurrentTime(newTime);
  }, [duration]);

  // Format time helper
  const formatTime = (time: number): string => {
    const minutes = Math.floor(time / 60);
    const seconds = Math.floor(time % 60);
    return `${minutes}:${seconds.toString().padStart(2, '0')}`;
  };

  // Handle play/pause click
  const handlePlayPauseClick = () => {
    if (isPlaying) {
      onPause();
    } else {
      onPlay();
    }
  };

  // Handle volume change
  const handleVolumeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const newVolume = Number(e.target.value);
    onVolumeChange?.(newVolume);
  };

  if (error) {
    return (
      <div className="bg-red-900/20 border border-red-600/30 rounded-lg p-4 text-center">
        <div className="text-red-400 text-lg mb-2">⚠️ Audio Error</div>
        <div className="text-red-300 text-sm">{error}</div>
        <div className="text-xs text-gray-600 mt-2">
          Track: {track.title} • ID: {track.sunoId || 'Unknown'}
        </div>
      </div>
    );
  }

  return (
    <div className="bg-gray-800 border border-gray-600 rounded-lg p-4 space-y-4">
      {/* Hidden audio element */}
      <audio
        ref={audioRef}
        onLoadedMetadata={handleLoadedMetadata}
        onTimeUpdate={handleTimeUpdate}
        onEnded={handleEnded}
        onError={(e) => handleError(e.nativeEvent)}
        preload="metadata"
      />

      {/* Track Info */}
      <div className="text-center">
        <h3 className="text-lg font-semibold text-white mb-1">{track.title}</h3>
        <p className="text-sm text-gray-400">
          {getStreamingCategory(track).replace('_', ' ').replace(/\b\w/g, l => l.toUpperCase())}
        </p>
        <p className="text-xs text-gray-500">
          {track.mood} • {track.genre} • {getEnergyLevel(track)} energy
        </p>
      </div>

      {/* Progress Bar */}
      <div className="space-y-2">
        <div className="flex justify-between text-xs text-gray-400">
          <span>{formatTime(currentTime)}</span>
          <span>{formatTime(duration)}</span>
        </div>
        <div
          className="relative h-2 bg-gray-700 rounded-full cursor-pointer"
          onClick={handleProgressClick}
        >
          <div
            className="absolute h-full bg-blue-600 rounded-full transition-all duration-100"
            style={{ width: `${duration ? (currentTime / duration) * 100 : 0}%` }}
          />
          <div
            className="absolute w-3 h-3 bg-blue-400 rounded-full -top-0.5 transform -translate-x-1/2 shadow-lg"
            style={{ left: `${duration ? (currentTime / duration) * 100 : 0}%` }}
          />
        </div>
      </div>

      {/* Controls */}
      <div className="flex items-center justify-center space-x-4">
        {/* Play/Pause Button */}
        <motion.button
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.95 }}
          onClick={handlePlayPauseClick}
          disabled={isLoading}
          className={`w-12 h-12 rounded-full flex items-center justify-center transition-all duration-200 ${
            isLoading
              ? 'bg-gray-600 cursor-not-allowed'
              : isPlaying
              ? 'bg-red-600 hover:bg-red-700'
              : 'bg-green-600 hover:bg-green-700'
          }`}
        >
          {isLoading ? (
            <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin" />
          ) : isPlaying ? (
            <svg className="w-5 h-5 text-white" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zM7 8a1 1 0 012 0v4a1 1 0 11-2 0V8zm5-1a1 1 0 00-1 1v4a1 1 0 102 0V8a1 1 0 00-1-1z" clipRule="evenodd" />
            </svg>
          ) : (
            <svg className="w-5 h-5 text-white ml-0.5" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM9.555 7.168A1 1 0 008 8v4a1 1 0 001.555.832l3-2a1 1 0 000-1.664l-3-2z" clipRule="evenodd" />
            </svg>
          )}
        </motion.button>

        {/* Volume Control */}
        <div className="flex items-center space-x-2">
          <svg className="w-4 h-4 text-gray-400" fill="currentColor" viewBox="0 0 20 20">
            <path fillRule="evenodd" d="M9.383 3.076A1 1 0 0110 4v12a1 1 0 01-1.617.793L4.5 13H2a1 1 0 01-1-1V8a1 1 0 011-1h2.5l3.883-2.793a1 1 0 011.617.793zM12.293 7.293a1 1 0 011.414 0L15 8.586l1.293-1.293a1 1 0 111.414 1.414L16.414 10l1.293 1.293a1 1 0 01-1.414 1.414L15 11.414l-1.293 1.293a1 1 0 01-1.414-1.414L13.586 10l-1.293-1.293a1 1 0 010-1.414z" clipRule="evenodd" />
          </svg>
          <input
            type="range"
            min="0"
            max="100"
            value={volume}
            onChange={handleVolumeChange}
            className="w-20 h-2 bg-gray-700 rounded-lg appearance-none cursor-pointer slider"
          />
          <span className="text-xs text-gray-400 w-8">{volume}%</span>
        </div>
      </div>

      {/* Status */}
      <div className="text-center">
        <div className="text-xs text-gray-500">
          {isLoading ? 'Loading...' : isPlaying ? 'Now Playing' : 'Ready to Play'}
        </div>
        {track.sunoId && (
          <div className="text-xs text-gray-600 mt-1">
            Suno ID: {track.sunoId}
          </div>
        )}
      </div>
    </div>
  );
};

export default SunoPlayer;
